	# create data dump folder and copy reference sequences
	$i=0;
	$parsed_input=$input;
	$parsed_input=~s/^.*[\\\/]//;
	while(1)
		{
		$i++;
		$dump_folder=$dump_folder_prefix."_$mday-$mon-$year"."_$parsed_input"."_#$i";
		if(-d$dump_folder)
			{
			next;
			}
		else
			{
			if(!$silent){print"\nCreate data dump folder $dump_folder...";}
			mkdir($dump_folder)||die print"\nERROR! Unable to create folder.\n$!\n\n";
			copy("UNITAS_refdump_$species/ncRNA.fas","$dump_folder/ncRNA.fas")||die print"\nERROR! Unable to copy UNITAS_refdump_$species/ncRNA.fas to dump folder.\n$!\n\n";
