				$user_refseq_added=0;
				if(!$silent){print"\nAdd sequences from files defined by user...";}
				open(NCRNA,">>$dump_folder/ncRNA.fas")||print"\nError! Failed to add user defined RefSeq to file $dump_folder/ncRNA.fas.\n$!\n\n";
				foreach$header(keys%user_refseq)
					{
					$user_refseq_added++;
					print NCRNA">$header\n$user_refseq{$header}\n";
					}
				close NCRNA;
				if(!$silent){print" OK. Added $user_refseq_added sequences.";}
				}
			if($species{$species}ne'undefined')
				{
				copy("UNITAS_refdump_$species/miRBase_mature","$dump_folder/miRBase_mature")||die print"\nERROR! Unable to copy UNITAS_refdump_$species/miRBase_mature to dump folder.\n$!\n\n";
				copy("UNITAS_refdump_$species/miRBase_hairpin_$species{$species}.fas","$dump_folder/miRBase_hairpin_$species{$species}.fas")||die print"\nERROR! Unable to copy UNITAS_refdump_$species/miRBase_hairpin_$species{$species}.fas to dump folder.\n$!\n\n";
				copy("UNITAS_refdump_$species/miRBase_hairpin_non-$species{$species}.fas","$dump_folder/miRBase_hairpin_non-$species{$species}.fas")||die print"\nERROR! Unable to copy UNITAS_refdump_$species/miRBase_hairpin_non-$species{$species}.fas to dump folder.\n$!\n\n";
				}
			if(-e"UNITAS_refdump_$species/piRclDB")
				{
				copy("UNITAS_refdump_$species/piRclDB","$dump_folder/piRclDB")||die print"\nERROR! Unable to copy UNITAS_refdump_$species/piRclDB to dump folder.\n$!\n\n";
				}
			if(!$silent){print" OK.";}
			last;
			}
		}

	# check total amount of reference sequence to estimate time to be used for SeqMap mapping
	$total_ncRNA_reference_bp=0;
	if(!$silent){print"\nCheck total amount of ncRNA reference sequence (excl. miRs)...";}
	open(REF,"$dump_folder/ncRNA.fas")||die print"\nERROR! Unable to open $dump_folder/ncRNA.fas.\n$!\n\n";
	while(<REF>)
		{
		if($_!~/^>/)
			{
			$_=~s/\s*$//;
			$total_ncRNA_reference_bp+=length$_;
			}
		}
	close REF;
	if(!$silent){print" $total_ncRNA_reference_bp bp.";}

	# check input file
	if(!$silent){print"\nCheck input file format...";}
	if(!-e$input)
		{
		die print"\nERROR! Input file $input not found.\n\n";
		}
	else
		{
		open(INPUT,$input)||die print"\nERROR! Unable to open input file $input.\n$!\n\n";
		$i=0;
		$fasta_heads=0;
		$fastq_heads=0;
		$sam_lines=0;
		$eland3_lines=0;
		$collapsed='collapsed ';
		$head_is_over=0;
		while(<INPUT>)
			{
			if($_!~/^trans_id/&&$_!~/^[#\@]/)# skip all the header lines in map files
				{
				$head_is_over=1;
				}
			next if($head_is_over==0);
			$_=~s/\s*$//;
			@d=split("\t",$_);
			$i++;
			last if($i==1000); # check 1000 lines after the header section
			if($_=~/^>/)
				{
				if($_!~/^>\s*\d+\s*$/)
					{
					$collapsed='uncollapsed ';
					}
				$fasta_heads++;
				}
			elsif($_=~/^@/)
				{
				if($_!~/^@\s*\d+\s*$/)
					{
					$collapsed='uncollapsed ';
					}
				$fastq_heads++;
				}
			elsif(looks_like_number($d[1])&&$d[1]>-1&&$d[1]<2**16&&$d[3]=~/^\d+$/&&$d[9]=~/^[ATGCUN]+$/)
				{
				$sam_lines++;
				}
			elsif($d[1]=~/^\d+$/&&$d[2]=~/^[ATGCUN]+$/&&$d[4]=~/^[ATGCUN]+$/&&$d[5]=~/^\d+$/&&$d[6]=~/^[\+-]$/)
				{
				$eland3_lines++;
				}
			}
		close INPUT;
		if($fasta_heads>$fastq_heads&&$fasta_heads>$sam_lines&&$fasta_heads>$eland3_lines)
			{
			$format='FASTA';
			}
		elsif($fastq_heads>$fasta_heads&&$fastq_heads>$sam_lines&&$fastq_heads>$eland3_lines)
			{
			$format='FASTQ';
			}
		elsif($sam_lines>$eland3_lines)
			{
			$format='SAM';
			$collapsed='';
			}
		elsif($eland3_lines>$sam_lines)
			{
			$format='ELAND3';
			$collapsed='';
			}
		else
			{
			print"\nERROR! Unknown file format of input file $input.\nUse FASTA, FASTQ, SAM or ELAND3 (from sRNAmapper: https://www.smallrnagroup.uni-mainz.de/software.html).\n\n";
			exit;
			}
		if(!$silent){print" ${collapsed}$format.";}
		close INPUT;

		if(@phasi>0&&$format ne'SAM'&&$format ne'ELAND3')
			{
			print"\nERROR! When searching for phased RNAs the input file must be a map file in SAM or ELAND3 format (ELAND3 is standard output format of sRNAmapper: https://www.smallrnagroup.uni-mainz.de/software.html).\n\n";
			exit;
			}
		if($check_pp_signature)
			{
			if($format ne'SAM'&&$format ne'ELAND3')
				{
				print"\nERROR! When searching for ping pong signatures the input file must be a map file in SAM or ELAND3 format (ELAND3 is standard output format of sRNAmapper: https://www.smallrnagroup.uni-mainz.de/software.html).\n\n";
				exit;
				}
			}
		if($do_trimming)
			{
			if($format eq'SAM'||$format eq'ELAND3')
				{
				print"\nNOTE! Do not use the trimming option (-trim) when the input file is a map file.\n\n";
				undef$do_trimming;
				}
			}
		}

	# convert FASTQ/SAM/ELAND3 to FASTA (SeqMap accepts FASTA only)
	if($format eq'FASTQ')
		{
		if(!$silent){print"\nConvert input file to FASTA...";}
		open(FASTQ,$input)||die print"\nUnable to open input file $input.\n$!\n\n";
		open(FASTA,">$dump_folder/$parsed_input.fas")||die print"\nError! Unable to create output file $dump_folder/$parsed_input.fas.\n$!\n\n";
		$i=0;
		@seq_data=();
		while(<FASTQ>)
			{
			$_=~s/\s*$//;
			push(@seq_data,$_);
			$i+=0.25;
			if($i==1)
				{
				$i=0;
				$seq_data[0]=~s/^\@/>/;
				if(length$seq_data[1]>0)
					{
					print FASTA"$seq_data[0]\n$seq_data[1]\n";
					}
				@seq_data=();
				}
			}
		close FASTA;
		close FASTQ;
		$parsed_input.=".fas";
		$input_for_DNApi=$input;
		$input="$dump_folder/$parsed_input";
		if(!$silent){print" OK.";}
		}
	elsif($format eq'SAM')
		{
		$input_map=$input;
		if(!$silent){print"\nExtract sequences from $input...";}
		# save flags that refer to '-' or '+' strand and mapped/unmapped reads in SAM files
		foreach$bit1(0,1)
		{foreach$bit2(0,2)
		{foreach$bit3(0,4)
		{foreach$bit4(0,8)
		{foreach$bit5(0,16)
		{foreach$bit6(0,32)
		{foreach$bit7(0,64)
		{foreach$bit8(0,128)
		{foreach$bit9(0,256)
		{foreach$bit10(0,512)
		{foreach$bit11(0,1024)
		{foreach$bit12(0,2048)
			{
			$flag=$bit1+$bit2+$bit3+$bit4+$bit5+$bit6+$bit7+$bit8+$bit9+$bit10+$bit11+$bit12;
			if($bit3==4)
				{
				$sam_mapped{$flag}=0;
				}
			else
				{
				$sam_mapped{$flag}=1;
				}
			if($bit5==16)
				{
				$sam_strand{$flag}='-';
				}
			else
				{
				$sam_strand{$flag}='+';
			}}}}}}}}}}}}}
		open(SAM,$input)||die print"\nERROR! Unable to open input file $input.\n$!\n\n";
		$extracted_sequences_nonid=0;
		$extracted_sequence_reads=0;
		$all_identifiers_are_numeric=1;
		while(<SAM>)
			{
			next if($_=~/^@/); # skip SAM header lines
			@sam=split("\t",$_);
			next if($sam_mapped{$sam[1]}==0||$sam[3]==0); # skip unmapped reads
			if(!looks_like_number($sam[0]))
				{
				$all_identifiers_are_numeric=0;
				}

			# reverse complement if sequence was mapped to the minus strand (SAM gives sequence of the locus, not of the mapped sequence)
			if($sam_strand{$sam[1]}eq'-')
				{
				$sam[9]=reverse$sam[9];
				$sam[9]=~tr/ATGC/TACG/;
				}

			$extracted_sequences{$sam[9]}{$sam[0]}++;
			}
		close SAM;

		# make a collapsed FASTA file from map file
		fasta_from_map_file();
		sub fasta_from_map_file
			{
			open(EXTRACTED,">$dump_folder/$parsed_input.mapped_sequences")||die print"\nError! Unable to create output file $dump_folder/$parsed_input.mapped_sequences.\n$!\n\n";
			if($all_identifiers_are_numeric==0)
				{
				foreach$seq(keys%extracted_sequences)
					{
					next if(length$seq==0);
					$read_counts=keys%{$extracted_sequences{$seq}};
					print EXTRACTED">$read_counts\n$seq\n";
					$input_map_seq{$seq}=$read_counts;
					$extracted_sequence_reads+=$read_counts;
					$extracted_sequences_nonid++;
					}
				}
			elsif($all_identifiers_are_numeric==1)
				{
				foreach$seq(keys%extracted_sequences)
					{
					next if(length$seq==0);
					foreach$val(keys%{$extracted_sequences{$seq}}) # there will be only one key!
						{
						$read_counts=$val;
						}
					print EXTRACTED">$read_counts\n$seq\n";
					$input_map_seq{$seq}=$read_counts;
					$extracted_sequence_reads+=$read_counts;
					$extracted_sequences_nonid++;
					}
				}
			$input="$dump_folder/$parsed_input.mapped_sequences";
			$collapsed='collapsed ';
			if(!$silent){print" done. $extracted_sequences_nonid non-identical sequences represented by $extracted_sequence_reads reads.";}
			if(@phasi==0&&!$check_pp_signature)
				{
				undef%extracted_sequences;
				undef%input_map_seq;
				}
			close EXTRACTED;
			}
		}
	elsif($format eq'ELAND3')
		{
		$input_map=$input;
		if(!$silent){print"\nExtract sequences from $input...";}
		open(ELAND3,$input)||die print"\nERROR! Unable to open input file $input.\n$!\n\n";
		%extracted_sequences=();
		$all_identifiers_are_numeric=1;
		while(<ELAND3>)
			{
			next if($_=~/^trans_id/||$_=~/^#/);
			@eland3=split("\t",$_);
			if(!looks_like_number($eland3[3]))
				{
				$all_identifiers_are_numeric=0;
				}
			$extracted_sequences{$eland3[4]}{$eland3[3]}++;
			}
		close ELAND3;
		fasta_from_map_file(); # see above (SAM)
		}

	# remove 3' adapter sequence
	if($do_trimming)
		{
		if($do_trimming=~/^[ATGCatgc]{5,}$/) # use pre-defined adapter sequence at least 5 nt in length
			{
			$adapter_to_clip=uc$do_trimming;
			}
		elsif($do_trimming eq'?') # search putative adapter sequence in input file
			{
			if(!$silent){print"\nSearch and remove 3' adapter sequences.";}
			my %motifs=();
			my %length=();
			my %new_3p=();
			my %trimmed_out=();

			# check for read length in input file, adjust adapter motif length
			open(TRIM,$input)||die print"\nERROR! Unable to open input file $input for trimming.\n$!\n\n";
			if(!$silent){print"\nCheck read length in input file (first $check_seq_for_size reads)...";}
			$i=0;
			%length=();
			while(<TRIM>)
				{
				if($_!~/^>/)
					{
					$i++;
					$_=~s/\s*$//;
					$length{length$_}++;
					last if($i==$check_seq_for_size);
					}
				}
			close TRIM;
			if(!$silent){print" OK.";}
			foreach$l(sort{$length{$a}<=>$length{$b}}keys%length)
				{
				$length=$l;
				if(!$silent){print"\n - length:$l nt -> $length{$l} reads.";}
				}
			if(keys%length==1)
				{
				if(!$silent){print"\nLooks good (untrimmed). All reads have equal length.\n";}
				$init_motif_length=$length-$ignore_5p;
				$min_motif_length=6;
				if($init_motif_length>12)
					{
					$init_motif_length=12;
					}
				if($init_motif_length<6)
					{
					$init_motif_length=5;
					$min_motif_length=4;
					$ignore_5p=$length-8;
					print"\n
  ------------------------------------------------------------------------
  WARNING: Reads in input file are very short. Searching for 3' adapter
  sequences is challenging, considering the typical miRNA length of 22 nt.
  Parameters are modified to search for frequent $init_motif_length nt motifs in last 8 nt
  of the reads. The final minimum motif length at 3' ends is set to $min_motif_length nt.

  NOTE:
  You may also consider to map your reads using a popular aligner such as
  STAR, bowtie or bowtie2, allowing softclipping at the 3' end. Then you
  can use the map file (SAM format) as input for unitas. We recommend this
  procedure for all unclipped sRNA datasets with sequence length <=26 nt.
  ------------------------------------------------------------------------

	";
					}
				if($init_motif_length<$min_motif_length)
					{
					$min_motif_length=$init_motif_length;
					}
				}
			else
				{
				if(!$silent){print"\nNOTE: Input file looks trimmed (found sequences with different length)!\nContinue nevertheless.\n";}
				$init_motif_length=12;
				$min_motif_length=6;
				}

			# search adapter sequence with DNApi
			$dnapi_success=0;
			if($format eq "FASTQ" && -e "dnapi.py" && -x "dnapi.py")
				{
				if(!$silent){print"\nSearch for adapter sequence with DNApi...";}
				if($^O eq'linux'||$^O eq'darwin')
					{
					qx/python dnapi.py $input_for_DNApi > $dump_folder\/adapter.dnapi/;
					}
				elsif($^O eq'MSWin32')
					{
					system("python dnapi.py $input_for_DNApi > $dump_folder/adapter.dnapi");
					}
				else
					{
					if(!$silent){print" WARNING! Unknown operating system.";}
					# try unix command
					qx/python dnapi.py $input_for_DNApi > $dump_folder\/adapter.dnapi/;
					if(!-e"$dump_folder/adapter.dnapi")
						{
						# try windows command
						system("python dnapi.py $input_for_DNApi > $dump_folder/adapter.dnapi");
						}
					}
				if(!-e"$dump_folder/adapter.dnapi")
					{
					if(!$silent){print"\nERROR. Tried to execute DNApi but cannot find its output file.\nWill use internal algorithm to search for 3' adapter sequence.";}
					}
				else
					{
					open(DNAPI,"$dump_folder/adapter.dnapi");
					while(<DNAPI>)
						{
						$_=~s/\s*$//;
						if($_=~/^[ATGC]+$/)
							{
							$dnapi_success=1;
							$adapter_abundance="??";
							$adapter_to_clip=$_;
							last;
							}
						}
					close DNAPI;
					if($dnapi_success==0)
						{
						if(!$silent){print"\nERROR. DNApi did not run properly (missing file?).\nWill use internal algorithm to search for 3' adapter sequence.";}
						}
					}
				}

			# check for motif (internal algorithm)
			if($dnapi_success==0)
				{
				search_motif_to_trim();
				sub search_motif_to_trim
					{
					$i=0;
					$j=0;
					open(TRIM,$input)||die print"\nERROR. Unable to open input file $input.\n$!\n\n";
					if(!$silent){print"\nSearch for frequent 3' motifs ($init_motif_length nt) in input file...";}
					while(<TRIM>)
						{
						if($_!~/^>/)
							{
							$i++;
							$_=~s/\s*$//;
							next if($i<=$skip_first_X_reads);
							$j++;
							last if($i==$check_seq+$skip_first_X_reads);
							$_=~s/^.{$ignore_5p}//;
							while(length$_>$init_motif_length)
								{
								$motifs{substr$_,0,$init_motif_length}++;
								$_=~s/.//;
								}
							}
						}
					close TRIM;
					}

				# repeat search without skipping first X reads if there are not enough reads in the input file
				if($j==0)
					{
					$skip_first_X_reads=0;
					search_motif_to_trim();
					}

				foreach$seq(sort{$motifs{$b}<=>$motifs{$a}}keys%motifs)
					{
					next if($seq=~/N/);
					next if($seq=~/^A+$/&&!$trim_polyA);
					next if($seq=~/^T+$/);
					next if($seq=~/^G+$/);
					next if($seq=~/^C+$/);
					$adapter_to_clip=$seq;
					$adapter_abundance=(int((($motifs{$seq}/$i)*10000)+0.5))/100;
					last;
					}
				if(!$silent){print" OK.";}
				if($adapter_abundance<$clipped_fraction_warning*100)
					{
					if(!$silent){print"\nNOTE: Fraction of reads containing putative adapter sequence is low!\nContinue nevertheless.\n";}
					}
				}
			}
		else{print"\n\nERROR. Predefined adapter sequence should not be shorter than 5 nt.\nUse '?' to let unitas search for unknown adapter sequences.\n\n";exit}

		# 1st round of motif clipping ("KNIPSER" by Daniel Gebert)
		open(TRIM,$input)||die print"\nERROR. Unable to open input file $input.\n$!\n\n";
		open(OUT,">$dump_folder/$parsed_input.trimmed")||die print"\nERROR. Unable to open output file $dump_folder/$parsed_input.trimmed.\n$!\n\n";
		if(!$silent&&$do_trimming eq'?'){print"\nClip most abundant sequence ($adapter_abundance%): $adapter_to_clip-(N)x-3'...";}
		elsif(!$silent&&$do_trimming=~/^[ATGCatgc]{5,}$/){print"\nClip pre-defined adapter sequence: $adapter_to_clip-(N)x-3'...";}
		$j=0; # fasta heads
		$k=0; # trimmed
		$k_ok=0; # trimmed, length ok
		$win_start=$min_size;
		while($line=<TRIM>)
			{
			$line=~s/\s*$//;
			if($line=~s/^>//)
				{
				$j++;
				$header = $line;
				}
			else
				{
				# Get sequence, adapter motif and max. Hamming Distance
				my $seq = $line;
				my $motif = $adapter_to_clip;
				my $hd_max = $adapter_mismatch;
				# Slide window through sequence and search for motif
				for (my $i=$win_start; $i <= length($seq)-$min_motif_length; $i++)
					{
						# Chop motif if window hits end of sequence
						if ($i > length($seq)-length($motif)) {
							chop($motif);
						}
						# Get window sequence
						my $window = substr($seq, $i, length $motif);
						# Check if motif matches window
						if ($window eq $motif) {
							$k++;
							if($i<=$max_size){
							$k_ok++;
							# Save clipped sequence and leave loop
							$clip_seq = substr($seq, 0, $i);
							$trimmed_out{$clip_seq}.="##$header";
							check_new_3p();
							last;
							}
						}
						# If no exact match found and mismatches allowed
						elsif ($hd_max > 0) {
							# Reduce max. Hamming Distance if motif chopped
							if ($motif ne $adapter_to_clip) { $hd_max = 1 }
							if (length($motif) < 9) { $hd_max = 0 }
							# Get Hamming Distance
							my $hd = ($window ^ $motif) =~ tr/\0//c;
							if ($hd <= $hd_max) {
								$k++;
								if($i<=$max_size){
								$k_ok++;
								# Save clipped sequence and leave loop
								$clip_seq = substr($seq, 0, $i);
								$trimmed_out{$clip_seq}.="##$header";
								check_new_3p();
								last;
								}
							}
						}
					}
				}
			}
		close TRIM;
		sub check_new_3p
			{
			foreach$p(1..$post_check_3p_ends)
				{
				$new_3p{-$p}{substr($clip_seq,-$p,1)}++;
				}
			}

		if(!$silent){print" OK.\n\nTotal processed reads: $j\nClipped reads: $k\n$min_size-$max_size nt: $k_ok\n";}
		if($k/$j<$clipped_fraction_warning)
			{
			if(!$silent){print"\nNOTE: Fraction of clipped reads is low!\nContinue nevertheless.\n";}
			}

		# refinement
		$i=0;
		$nibble=0;
		$nibble_motif="";
		if(!$silent){print"\nCheck new 3' ends for bias in positional nucleotide composition.\nNucleotide compositions of new 3' ends [%]:";}
		foreach$p(1..$post_check_3p_ends)
			{
			$i++;
			$N=$new_3p{-$p}{'A'}+$new_3p{-$p}{'T'}+$new_3p{-$p}{'G'}+$new_3p{-$p}{'C'};
			$A_perc=(int((($new_3p{-$p}{'A'}/$N)*10000)+0.5))/100;
			$T_perc=(int((($new_3p{-$p}{'T'}/$N)*10000)+0.5))/100;
			$G_perc=(int((($new_3p{-$p}{'G'}/$N)*10000)+0.5))/100;
			$C_perc=(int((($new_3p{-$p}{'C'}/$N)*10000)+0.5))/100;
			if(!$silent){print"\nPosition -$p: A:$A_perc T:$T_perc G:$G_perc C:$C_perc";}
			if($new_3p{-$p}{'A'}/$N>$refinement_threshold)
				{
				$nibble++;
				$nibble_motif="A".$nibble_motif;
				if(!$silent){print"\tBAD! Too much 'A'!";}
				}
			elsif($new_3p{-$p}{'T'}/$N>$refinement_threshold)
				{
				$nibble++;
				$nibble_motif="T".$nibble_motif;
				if(!$silent){print"\tBAD! Too much 'T'!";}
				}
			elsif($new_3p{-$p}{'G'}/$N>$refinement_threshold)
				{
				$nibble++;
				$nibble_motif="G".$nibble_motif;
				if(!$silent){print"\tBAD! Too much 'G'!";}
				}
			elsif($new_3p{-$p}{'C'}/$N>$refinement_threshold)
				{
				$nibble++;
				$nibble_motif="C".$nibble_motif;
				if(!$silent){print"\tBAD! Too much 'C'!";}
				}
			else
				{
				if(!$silent){print"\tOK.";}
				last;
				}
			}
		if($i==$post_check_3p_ends)
			{
			if(!$silent){print"\nNOTE: Sequence file needs attention!\nLow heterogenety in last $post_check_3p_ends nucleotides!\nTrimming is presumably not correct!\nContinue nevertheless.\n\n";}
			}
			$i=0;
		if($nibble==0)
			{
			if(!$silent){print"\n\n3' ends of processed sequences look fine.\nPrint sequences to $input.trimmed...";}
			foreach$seq(keys%trimmed_out)
				{
				$trimmed_out{$seq}=~s/^##//;
				@titles=split('##',$trimmed_out{$seq});
				foreach$title(@titles)
					{
					$i++;
					print OUT">$title\n$seq\n";
					}
				}
			if(!$silent){print" OK.";}
			}
		else
			{
			if(!$silent){print"\n\n3' ends of processed sequences need further refinement.\nRemove $nibble_motif-3' and print sequences to $input.trimmed...";}
			foreach$seq(keys%trimmed_out)
				{
				$trimmed_seq=$seq;
				if($trimmed_seq=~s/$nibble_motif$//)
					{
					if(length$trimmed_seq>=$min_size)
						{
						$trimmed_out{$seq}=~s/^##//;
						@titles=split('##',$trimmed_out{$seq});
						foreach$title(@titles)
							{
							$i++;
							print OUT">$title\n$trimmed_seq\n";
							}
						}
					}
				}
			if(!$silent){print"\nRemaining reads $min_size-$max_size nt: $i";}
			}
		close OUT;
		if(!$silent){print"\n\nDetermined adapter sequence: ${nibble_motif}${adapter_to_clip}-(N)x-3'.\n";}
		$input=$trimmmed_file;
		$parsed_input.=".trimmed";
		$input="$dump_folder/$parsed_input";
		}

	# collapse if input is uncollapsed FASTA or FASTQ (or map file from uncollapsed FASTA or FASTQ)
	if($collapsed eq'uncollapsed ')
		{
		if(!$silent){print"\nCollapse input file...";}
		open(INPUT,$input)||die print"\nError! Unable to open input file $input.\n$!\n\n";
		open(COLLAPSED,">$dump_folder/$parsed_input.collapsed")||die print"\nError! Unable to create input file $dump_folder/$parsed_input.\n$!\n\n";
		while(<INPUT>)
			{
			$_=~s/\s*$//;
			if($_!~/^>/)
				{
				$input{$_}++;
				}
			}
		foreach$seq(sort{$input{$b}<=>$input{$a}}keys%input)
			{
			next if(length$seq==0);
			print COLLAPSED">$input{$seq}\n$seq\n";
			}
		close INPUT;
		close COLLAPSED;
		$parsed_input.=".collapsed";
		$input="$dump_folder/$parsed_input";
		undef%input;
		if(!$silent){print" OK.";}
		}

	# low complexity filter | use default parameters from the duster tool (NGS TOOLBOX)
	if(!$skip_dust)
		{
		if(!$silent){print"\nFilter low complexity reads from $parsed_input...";}
		open(IN,$input)||die print"\nError! Unable to open input file $input.\n$!\n\n";
		open(OK,">$dump_folder/$parsed_input.no-dust")||die print"\nError! Unable to create file $dump_folder/$parsed_input.no-dust.\n$!\n\n";
		open(DUST,">$dump_folder/$parsed_input.dust")||die print"\nError! Unable to create file $dump_folder/$parsed_input.dust.\n$!\n\n";
		$i=0;
		$simple_reads=0;
		$complex_reads=0;
		$threshold_2nt=$dust_limit+((1-$dust_limit)*$dust_limit);
		$simple_1nt=0;
		$simple_2nt=0;
		my%simple_motif=();
		@seq_data=();
		while(<IN>)
			{
			$_=~s/\s*$//;
			if($_=~s/^>//)
				{
				$reads=$_;
				}
			else
				{
				$ok=1;
				next if(length$_==0);
				$read_length=length$_;

				# simple monomer over-representation check
				$monomer_check=$_;
				$amount_N=($monomer_check=~tr/N//);
				$non_N=length$monomer_check;
				$amount_A=($monomer_check=~tr/A//);
				$amount_T=($monomer_check=~tr/T//);
				$amount_G=($monomer_check=~tr/G//);
				$amount_C=($monomer_check=~tr/C//);
				if($non_N/$read_length<$dust_maxN||$amount_A/$non_N>$dust_limit||$amount_T/$non_N>$dust_limit||$amount_G/$non_N>$dust_limit||$amount_C/$non_N>$dust_limit)
					{
					$ok=0;
					$simple_1nt++;
					}

				# motif enrichment check
				if($ok==1)
					{
					my%fragments=();
					foreach$frag_size(2..$dust_max)
						{
						$gnaw=$_;
						while(length$gnaw>=$frag_size)
							{
							$gnaw=~s/^.{$frag_size}//;
							$fragments{$&}=$frag_size;
							}
						}
					foreach$fragment(sort{$fragments{$a}<=>$fragments{$b}}keys%fragments) # make it faster by searching for short motifs first
						{
						$check=$_;
						$check=~s/$fragment//g;
						if(length$check<=$read_length*(1-$dust_limit))
							{
							$ok=0;
							$simple_motif{length$fragment}++;
							last;
							}
						}
					undef%fragments;
					}

				# 2 bases over-representation check
				if($ok==1&&!$no_overrepresentation_check)
					{
					if(($amount_A+$amount_T)/$non_N>$threshold_2nt||($amount_A+$amount_G)/$non_N>$threshold_2nt||($amount_A+$amount_C)/$non_N>$threshold_2nt||($amount_T+$amount_G)/$non_N>$threshold_2nt||($amount_T+$amount_C)/$non_N>$threshold_2nt||($amount_G+$amount_C)/$non_N>$threshold_2nt)
						{
						$ok=0;
						$simple_2nt++;
						}
					}

				$output=">$reads\n$_\n";
				if($ok==1)
					{
					$complex_reads+=$reads;
					print OK$output;
					$global_annotation_matrix{$_}{$reads}.="";
					}
				else
					{
					$simple_reads+=$reads;
					print DUST$output;
					$global_annotation_matrix{$_}{$reads}="low_complexity\t";
					}
				}
			}
		$parsed_input.=".no-dust";
		$input="$dump_folder/$parsed_input";
		if(!$silent){print" done.\n$complex_reads sequences are ok.\n$simple_reads sequences are dusty (low-complexity).\n  Monomer:             $simple_1nt\n  Biased for 2 bases:  $simple_2nt";}
		if(!$silent)
			{
			foreach$i(2..$dust_max)
				{
				if(!$simple_motif{$i})
					{
					$simple_motif{$i}=0;
					}
				print"\n  $i nt motif enriched: $simple_motif{$i}";
				}
			}
		if($complex_reads==0)
			{
			print"\nNo reads passed low complexity filter!";
			exit;
			}
		}
	close IN;
	close OK;
	close DUST;

	# load mature miRNA sequences to memory
	$no_nonmiRs=0;
	if(!$skip_miR)
		{
		if(!$silent){print"\nLoad mature miRNA sequences for $species{$species} to memory...";}
		open(MIR_MAT,"$dump_folder/miRBase_mature")||die print"\nERROR! Unable to open miRBase file $dump_folder/miRBase_mature.\n$!\n\n";
		$i=0;
		while(<MIR_MAT>)
			{
			$_=~s/\s*$//;
			if($_=~s/^>//)
				{
				@head=split(' ',$_);
				}
			else
				{
				$_=~s/U/T/g;
				if(lc$head[2].'_'.$head[3]eq$species)
					{
					$i++;
					$miRBase_species{$_}=$head[4];
					}
				}
			}
		close MIR_MAT;
		if(!$silent){print" OK ($i sequences loaded).\nLoad mature miRNA sequences for other species to memory...";}
		open(MIR_MAT,"$dump_folder/miRBase_mature")||die print"\nERROR! Unable to open miRBase file $dump_folder/miRBase_mature.\n$!\n\n";
		$i=0;
		while(<MIR_MAT>)
			{
			$_=~s/\s*$//;
			if($_=~s/^>//)
				{
				@head=split(' ',$_);
				}
			else
				{
				$_=~s/U/T/g;
				if(!$miRBase_species{$_})
					{
					$i++;
					$miRBase_nonspecies{$_}.="$head[0] ";
					}
				}
			}
		close MIR_MAT;
		foreach(keys%miRBase_nonspecies)
			{
			$miRBase_nonspecies{$_}=~s/ $//;
			}
		if(!$silent){print" OK ($i sequences loaded).";}

		# search mature canonical miRNAs, perfect full-length hits.
		if(!$silent){print"\nSearch for mature, canonical miRNAs...";}
		open(OUT_MIR_SPECIES,">$dump_folder/$outfile_prefix.miR.$species{$species}.fas")||die print"\nERROR! Unable to create output file $dump_folder/$outfile_prefix.mature-miR.$species{$species}.fas.\n$!\n\n";
		open(OUT_MIR_NONSPECIES,">$dump_folder/$outfile_prefix.miR.non-$species{$species}.fas")||die print"\nERROR! Unable to create output file $dump_folder/$outfile_prefix.mature-miR.non-$species{$species}.fas.\n$!\n\n";
		open(OUT_NONMIR,">$dump_folder/$outfile_prefix.non-miR.fas")||die print"\nERROR! Unable to create output file $dump_folder/$outfile_prefix.non-miR.fas.\n$!\n\n";
		open(INPUT,$input)||die print" ERROR! Unable to open input file $input.\n$!\n\n";
		while(<INPUT>)
			{
			$_=~s/\s*$//;
			if($_=~s/^>//)
				{
				$reads=$_;
				}
			elsif($miRBase_species{$_})
				{
				$miR_canonical_species{$_}=$reads;
				$miR_table_out_species{$_}{0}=$reads;
				$output_miR_species{$_}=1;
				if($global_annotation_matrix{$_}{$reads}!~/\Q$miRBase_species{$_}/){$global_annotation_matrix{$_}{$reads}.="$miRBase_species{$_}\t";}
				}
			elsif($miR_nonspecies{$_})
				{
				$miR_canonical_nonspecies{$_}=$reads;
				$miR_table_out_nonspecies{$_}{0}=$reads;
				$output_miR_nonspecies{$_}=1;
				if($global_annotation_matrix{$_}{$reads}!~/\Q$miR_nonspecies{$_}/){$global_annotation_matrix{$_}{$reads}.="$miR_nonspecies{$_}\t";}
				}
			else
				{
				$non_annotated{$_}=$reads;
				}
			}
		close INPUT;
		if(!$silent){print" OK.\nSearch for non-canonical and 3' tailed miRNAs ($species{$species})...";}

		# create SeqMap input file for SPECIES run
		open(INPUT,$input)||die print"\nERROR! Unable to open input file $input.\n$!\n\n";
		open(NEW_INPUT,">$dump_folder/SeqMap_input1.temp")||die print"\nERROR! Unable to create temporary SeqMap input file $dump_folder/SeqMap_input1.temp.\n$!\n\n";
		while($input_line=<INPUT>)
			{
			$input_line=~s/\s*$//;
			if($input_line=~s/^>//)
				{
				$reads=$input_line;
				}
			elsif(!$miR_canonical_species{$input_line})
				{
				print NEW_INPUT">$reads\n$input_line\n";
				}
			}
		close INPUT;
		close NEW_INPUT;

		# check length of miRNA precursors
		%miR_hairpin_length=();
		foreach$index("$species{$species}","non-$species{$species}")
			{
			open(MIR_PREC,"$dump_folder/miRBase_hairpin_$index.fas")||die print"\nERROR! Unable to check miR-precursor lengths from $dump_folder/miRBase_hairpin_$index.fas.\n$!\n\n";
			while(<MIR_PREC>)
				{
				$_=~s/\s*$//;
				if($_=~s/^>//)
					{
					$head=$_;
					}
				else
					{
					$miR_hairpin_length{$head}=length$_;
					}
				}
			close MIR_PREC;
			}

		# run SeqMap on SPECIES miRNA hairpins
		$mm_for_SeqMap_run=$intmod_miR+$max_miRtail_length;
		if($^O eq'linux'||$^O eq'darwin')
			{
			qx/.\/seqmap.exe $mm_for_SeqMap_run $dump_folder\/SeqMap_input1.temp $dump_folder\/miRBase_hairpin_$species{$species}.fas $dump_folder\/SeqMap_species.temp \/output_all_matches \/forward_strand \/available_memory:${memory}000 $less_memory_options_miR> $dump_folder\/SeqMap_report1.txt/;
			}
		elsif($^O eq'MSWin32')
			{
			system("seqmap.exe $mm_for_SeqMap_run $dump_folder/SeqMap_input1.temp $dump_folder/miRBase_hairpin_$species{$species}.fas $dump_folder/SeqMap_species.temp /output_all_matches /forward_strand /available_memory:${memory}000 ${less_memory_options_miR}2>&1 > $dump_folder/SeqMap_report1.txt");
			}
		else
			{
			if(!$silent){print" WARNING! Unknown operating system.";}
			# try unix command
			qx/.\/seqmap.exe $mm_for_SeqMap_run $dump_folder\/SeqMap_input1.temp $dump_folder\/miRBase_hairpin_$species{$species}.fas $dump_folder\/SeqMap_species.temp \/output_all_matches \/forward_strand \/available_memory:${memory}000 $less_memory_options_miR> $dump_folder\/SeqMap_report1.txt/;
			if(!-e"$dump_folder/SeqMap_species.temp")
				{
				# try windows command
				system("seqmap.exe $mm_for_SeqMap_run $dump_folder/SeqMap_input1.temp $dump_folder/miRBase_hairpin_$species{$species}.fas $dump_folder/SeqMap_species.temp /output_all_matches /forward_strand /available_memory:${memory}000 ${less_memory_options_miR}2>&1 > $dump_folder/SeqMap_report1.txt");
				}
			}
		# check SeqMap report
		open(SMR,"$dump_folder/SeqMap_report1.txt");
		while(<SMR>)
			{
			if($_=~/not enough memory/)
				{
				print"\n\nERROR. Not enough memory available for SeqMap. Check $dump_folder/SeqMap_report1.txt for more details. Try less mismatch (-mismatch [n]), less insertions/deletions (-insdel [n]), fewer non-template 3' nucleotides (-tail [n]). Alternatively you may want to use the options -less_memory or -even_less_memory.\n\n";
				exit;
				}
			}
		close SMR;

		# clean up SeqMap output for SPECIES
		$file_id="species";
		clean_SeqMap_miRNA();
		open(SEQMAP,"$dump_folder/SeqMap_species.clean-2.temp");
		%hits_per_miRspecies=();
		while(<SEQMAP>)
			{
			$_=~s/\s*$//;
			@seqmap=split("\t",$_);
			$hits_per_miRspecies{$seqmap[4]}++;

			# save targets of this sequence (without the tail)
			if(!$targets_per_miRspecies{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])})
				{
				$targets_per_miRspecies{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])}.="$seqmap[0] ";
				}

			# save valid hits to exclude these from non-species search
			$valid_species_hits{$seqmap[4]}=1;
			}
		close SEQMAP;

		# search non-canonical (tailed and untailed) SPECIES miRNAs
		unless(open(SEQMAP,"$dump_folder/SeqMap_species.clean-2.temp"))
			{
			print"\nERROR! Unable to open temporary SeqMap output file.\n$!\n\n";
			open(REPORT,"$dump_folder/SeqMap_report1.txt");
			while(<REPORT>)
				{
				print$_;
				}
			exit;
			}
		while(<SEQMAP>)
			{
			$_=~s/\s*$//;
			@seqmap=split("\t",$_);

			# count internal modifications
			if($seqmap[0]=~/\(.+\)/)
				{
				$mod=$&;
				$mod=~s/[\(\)]//g;
				$mod=~s/\d+//;
				$modpos=$&;
				$mod=~s/^-/?-/;
				$count_internal_mods_species{$mod}+=$seqmap[3]/$hits_per_miRspecies{$seqmap[4]};
				$count_internal_mods_species{$modpos}+=$seqmap[3]/$hits_per_miRspecies{$seqmap[4]};
				$mods_per_pos_species{$modpos}{$mod}+=$seqmap[3]/$hits_per_miRspecies{$seqmap[4]};
				}

			if($global_annotation_matrix{$seqmap[4]}{$seqmap[3]}!~/\Q$seqmap[0]/){$global_annotation_matrix{$seqmap[4]}{$seqmap[3]}.="$seqmap[0]\t";}

			# perfect hit to species precursor but not to canonical miRNA according to miRBase
			if($seqmap[6]==0&&!$miRBase_species{$seqmap[4]})
				{
				$miR_noncanonical_species{$seqmap[4]}+=$seqmap[3]/$hits_per_miRspecies{$seqmap[4]};
				$miR_table_out_species{$seqmap[4]}{0}+=$seqmap[3]/$hits_per_miRspecies{$seqmap[4]};
				$output_miR_species{$seqmap[4]}=1;
				delete$miR_canonical_nonspecies{$seqmap[4]};
				delete$miR_table_out_nonspecies{$seqmap[4]};
				delete$output_miR_nonspecies{$seqmap[4]};
				}

			# not tailed, only internal modification
			elsif($seqmap[5]==0&&!$miRBase_species{$seqmap[4]})
				{
				$miR_noncanonical_species{$seqmap[4]}+=$seqmap[3]/$hits_per_miRspecies{$seqmap[4]};
				$miR_table_out_species{$seqmap[4]}{0}+=$seqmap[3]/$hits_per_miRspecies{$seqmap[4]};
				$output_miR_species{$seqmap[4]}=1;
				delete$miR_canonical_nonspecies{$seqmap[4]};
				delete$miR_table_out_nonspecies{$seqmap[4]};
				delete$output_miR_nonspecies{$seqmap[4]};
				}

			# check for 3' tailed hit to species precursor
			else
				{
				$output_miR_species{$seqmap[4]}=1;
				$tail=substr($seqmap[4],-$seqmap[5]);
				$tails_species{$tail}+=$seqmap[3]/$hits_per_miRspecies{$seqmap[4]};
				$tails_per_miR_species{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])}{$tail}+=$seqmap[3]/$hits_per_miRspecies{$seqmap[4]};

				# check if this is a tailed canonical miRNA according to miRBase
				if($miRBase_species{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])})
					{
					$miR_table_out_species{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])}{length$tail}+=$seqmap[3]/$hits_per_miRspecies{$seqmap[4]};
					$miR_canonical_species{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])}+=$seqmap[3]/$hits_per_miRspecies{$seqmap[4]};
					}

				# this is a tailed non-canonical miRNA according to miRBase
				else
					{
					$miR_table_out_species{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])}{length$tail}+=$seqmap[3]/$hits_per_miRspecies{$seqmap[4]};
					$miR_noncanonical_species{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])}+=$seqmap[3]/$hits_per_miRspecies{$seqmap[4]};
					}

				# remove tailed and non-tailed versions of this miRNA from non-species and non-annotated hashes
				delete$miR_canonical_nonspecies{$seqmap[4]};
				delete$miR_table_out_nonspecies{$seqmap[4]};
				delete$output_miR_nonspecies{$seqmap[4]};
				delete$miR_canonical_nonspecies{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])};
				delete$miR_table_out_nonspecies{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])};
				delete$output_miR_nonspecies{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])};
				}
			}
		close SEQMAP;
		undef%hits_per_miRspecies;

		if(!$search_species_miRs_only)
			{
			# create SeqMap input file for NON-SPECIES run
			if(!$silent){print" OK.\nSearch for non-canonical and 3' tailed miRNAs (non-$species{$species})...";}
			open(INPUT,"$dump_folder/SeqMap_input1.temp")||die print"\nERROR! Unable to open temporary SeqMap input file $dump_folder/SeqMap_input1.temp.\n$!\n\n";
			open(NEW_INPUT,">$dump_folder/SeqMap_input2.temp")||die print"\nERROR! Unable to create temporary SeqMap input file $dump_folder/SeqMap_input2.temp.\n$!\n\n";
			$n_remaining_seqs=0;
			while(<INPUT>)
				{
				$_=~s/\s*$//;
				if($_=~s/^>//)
					{
					$reads=$_;
					}
				elsif(!$valid_species_hits{$_}&&!$miR_canonical_nonspecies{$_})
					{
					$n_remaining_seqs++;
					print NEW_INPUT">$reads\n$_\n";
					}
				}
			close INPUT;
			close NEW_INPUT;

			if($n_remaining_seqs>0)
				{
				# run SeqMap on NON-SPECIES miRNA hairpins IF THERE ARE SEQUENCES THAT COULD NOT BE ANNOTATED AS SPECIES MIR!!!
				if($^O eq'linux'||$^O eq'darwin')
					{
					qx/.\/seqmap.exe $mm_for_SeqMap_run $dump_folder\/SeqMap_input2.temp $dump_folder\/miRBase_hairpin_non-$species{$species}.fas $dump_folder\/SeqMap_nonspecies.temp \/output_all_matches \/forward_strand \/available_memory:${memory}000 $less_memory_options_miR> $dump_folder\/SeqMap_report2.txt/;
					}
				elsif($^O eq'MSWin32')
					{
					system("seqmap.exe $mm_for_SeqMap_run $dump_folder/SeqMap_input2.temp $dump_folder/miRBase_hairpin_non-$species{$species}.fas $dump_folder/SeqMap_nonspecies.temp /output_all_matches /forward_strand /available_memory:${memory}000 ${less_memory_options_miR}2>&1 > $dump_folder/SeqMap_report2.txt");
					}
				else
					{
					if(!$silent){print" WARNING! Unknown operating system.";}
					# try unix command
					qx/.\/seqmap.exe $mm_for_SeqMap_run $dump_folder\/SeqMap_input2.temp $dump_folder\/miRBase_hairpin_non-$species{$species}.fas $dump_folder\/SeqMap_nonspecies.temp \/output_all_matches \/forward_strand \/available_memory:${memory}000 $less_memory_options_miR> $dump_folder\/SeqMap_report2.txt/;
					if(!-e"$dump_folder/SeqMap_nonspecies.temp")
						{
						# try windows command
						system("seqmap.exe $mm_for_SeqMap_run $dump_folder/SeqMap_input2.temp $dump_folder/miRBase_hairpin_non-$species{$species}.fas $dump_folder/SeqMap_nonspecies.temp /output_all_matches /forward_strand /available_memory:${memory}000 ${less_memory_options_miR}2>&1 > $dump_folder/SeqMap_report2.txt");
						}
					}
				# check SeqMap report
				open(SMR,"$dump_folder/SeqMap_report2.txt");
				while(<SMR>)
					{
					if($_=~/not enough memory/)
						{
						print"\n\nERROR. Not enough memory available for SeqMap. Check $dump_folder/SeqMap_report2.txt for more details. Try less mismatch (-mismatch [n]), less insertions/deletions (-insdel [n]), fewer non-template 3' nucleotides (-tail [n]). Alternatively you may want to use the options -less_memory or -even_less_memory.\n\n";
						exit;
						}
					}
				close SMR;

				# clean up SeqMap output
				$file_id="nonspecies";
				clean_SeqMap_miRNA();
				open(SEQMAP,"$dump_folder/SeqMap_nonspecies.clean-2.temp");
				%hits_per_miRnonspecies=();
				while(<SEQMAP>)
					{
					$_=~s/\s*$//;
					@seqmap=split("\t",$_);
					$hits_per_miRnonspecies{$seqmap[4]}++;

					# save targets of this sequence (without the tail)
					if(!$targets_per_miRnonspecies{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])})
						{
						$targets_per_miRnonspecies{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])}.="$seqmap[0] ";
						}
					}
				close SEQMAP;

				# search non-canonical (tailed and untailed) NON-SPECIES miRNAs
				unless(open(SEQMAP,"$dump_folder/SeqMap_nonspecies.clean-2.temp"))
					{
					print"\nERROR! Unable to open temporary SeqMap output file.\n$!\n\n";
					open(REPORT,"$dump_folder/SeqMap_report2.txt");
					while(<REPORT>)
						{
						print$_;
						}
					exit;
					}
				while(<SEQMAP>)
					{
					$_=~s/\s*$//;
					@seqmap=split("\t",$_);

					# count internal modifications
					if($seqmap[0]=~/\(.+\)/)
						{
						$mod=$&;
						$mod=~s/[\(\)]//g;
						$mod=~s/\d+//;
						$modpos=$&;
						$mod=~s/^-/?-/;
						$count_internal_mods_nonspecies{$mod}+=$seqmap[3]/$hits_per_miRnonspecies{$seqmap[4]};
						$count_internal_mods_nonspecies{$modpos}+=$seqmap[3]/$hits_per_miRnonspecies{$seqmap[4]};
						$mods_per_pos_nonspecies{$modpos}{$mod}+=$seqmap[3]/$hits_per_miRnonspecies{$seqmap[4]};
						}

					if($global_annotation_matrix{$seqmap[4]}{$seqmap[3]}!~/\Q$seqmap[0]/){$global_annotation_matrix{$seqmap[4]}{$seqmap[3]}.="$seqmap[0]\t";}

					# perfect hit to non-species precursor but not to canonical non-species miRNA according to miRBase
					if($seqmap[6]==0&&!$miRBase_nonspecies{$seqmap[4]})
						{
						$miR_noncanonical_nonspecies{$seqmap[4]}+=$seqmap[3]/$hits_per_miRnonspecies{$seqmap[4]};
						$miR_table_out_nonspecies{$seqmap[4]}{0}+=$seqmap[3]/$hits_per_miRnonspecies{$seqmap[4]};
						$output_miR_nonspecies{$seqmap[4]}=1;
						}

					# not tailed, only internal modification
					elsif($seqmap[5]==0&&!$miRBase_nonspecies{$seqmap[4]})
						{
						$miR_noncanonical_nonspecies{$seqmap[4]}+=$seqmap[3]/$hits_per_miRnonspecies{$seqmap[4]};
						$miR_table_out_nonspecies{$seqmap[4]}{0}+=$seqmap[3]/$hits_per_miRnonspecies{$seqmap[4]};
						$output_miR_nonspecies{$seqmap[4]}=1;
						}

					# check for 3' tailed hit to non-species precursor
					else
						{
						$output_miR_nonspecies{$seqmap[4]}=1;
						$tail=substr($seqmap[4],-$seqmap[5]);
						$tails_nonspecies{$tail}+=$seqmap[3]/$hits_per_miRnonspecies{$seqmap[4]};
						$tails_per_miR_nonspecies{substr($seqmap[4],0,$p)}{$tail}+=$seqmap[3]/$hits_per_miRnonspecies{$seqmap[4]};

						# check if this is a tailed canonical non-species miRNA
						if($miRBase_nonspecies{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])})
							{
							$miR_table_out_nonspecies{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])}{length$tail}+=$seqmap[3]/$hits_per_miRnonspecies{$seqmap[4]};
							$miR_canonical_nonspecies{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])}+=$seqmap[3]/$hits_per_miRnonspecies{$seqmap[4]};
							}
						# this must be a tailed non-canonical non-species miRNA
						else
							{
							$miR_table_out_nonspecies{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])}{length$tail}+=$seqmap[3]/$hits_per_miRnonspecies{$seqmap[4]};
							$miR_noncanonical_nonspecies{substr($seqmap[4],0,(length$seqmap[4])-$seqmap[5])}+=$seqmap[3]/$hits_per_miRnonspecies{$seqmap[4]};
							}
						}
					}
				close SEQMAP;
				undef%hits_per_miRnonspecies;
				}
			}

		# make miRNA output files
		$i_miRspecies=0;
		$i_miRnonspecies=0;
		$nonid_miRspecies=0;
		$nonid_miRnonspecies=0;
		open(INPUT,$input)||die print"\nERROR! Unable to open input file $input.\n$!\n\n";
		while(<INPUT>)
			{
			$_=~s/\s*$//;
			if($_=~s/^>//)
				{
				$reads=$_;
				}
			else
				{
				if($output_miR_species{$_})
					{
					$nonid_miRspecies++;
					$i_miRspecies+=$reads;
					$miR_spec_seq{$_}=$reads;
					}
				elsif($output_miR_nonspecies{$_})
					{
					$nonid_miRnonspecies++;
					$i_miRnonspecies+=$reads;
					$miR_nonspec_seq{$_}=$reads;
					}
				else
					{
					$nonmiR_seq{$_}=$reads;
					}
				}
			}
		close INPUT;
		foreach(sort{$miR_spec_seq{$b}<=>$miR_spec_seq{$a} or $a cmp $b}keys%miR_spec_seq)
			{
			print OUT_MIR_SPECIES">$miR_spec_seq{$_}\n$_\n";
			}
		foreach(sort{$miR_nonspec_seq{$b}<=>$miR_nonspec_seq{$a} or $a cmp $b}keys%miR_nonspec_seq)
			{
			print OUT_MIR_NONSPECIES">$miR_nonspec_seq{$_}\n$_\n";
			}
		foreach(sort{$nonmiR_seq{$b}<=>$nonmiR_seq{$a} or $a cmp $b}keys%nonmiR_seq)
			{
			print OUT_NONMIR">$nonmiR_seq{$_}\n$_\n";
			}

		if(keys%nonmiR_seq==0)
			{
			$no_nonmiRs=1; # skip ncRNA annotation when non-miR fasta file is empty
			}
		undef%miR_spec_seq;
		undef%miR_nonspec_seq;
		undef%nonmiR_seq;

		close OUT_MIR_SPECIES;
		close OUT_MIR_NONSPECIES;
		close OUT_NONMIR;
		$ba_in="$dump_folder/$outfile_prefix.miR.non-$species{$species}.fas";
		$ba_out="$dump_folder/$outfile_prefix.miR.non-$species{$species}.info";
		basic_analysis();
		$ba_in="$dump_folder/$outfile_prefix.miR.$species{$species}.fas";
		$ba_out="$dump_folder/$outfile_prefix.miR.$species{$species}.info";
		basic_analysis();

		# make miRNA-modifications files
		open(OUT_MODS_SPECIES,">$dump_folder/$outfile_prefix.miR-modifications_$species{$species}.txt")||die print"\nERROR! Unable to create output file $dump_folder/$outfile_prefix.miR-modifications_$species{$species}.txt.\n$!\n\n";
		open(OUT_MODS_NONSPECIES,">$dump_folder/$outfile_prefix.miR-modifications_non-$species{$species}.txt")||die print"\nERROR! Unable to create output file $dump_folder/$outfile_prefix.miR-modifications_non-$species{$species}.txt.\n$!\n\n";
		print OUT_MODS_SPECIES"3'-tailings (non-template nucleotides)\n";
		print OUT_MODS_NONSPECIES"3'-tailings (non-template nucleotides)\n";
		%new=();
		%old=%miRtails;
		foreach(1..$max_miRtail_length-1)
			{
			%new=%old;
			foreach$nt(keys%miRtails)
				{
				foreach(keys%old)
					{
					$new{$_.$nt}=1;
					}
				}
			%old=(%old,%new)
			}
		@combs=keys%old;
		@combs=sort@combs;
		foreach$tail(@combs)
			{
			if(!$tails_species{$tail})
				{
				$tails_species{$tail}=0;
				}
			if(!$tails_nonspecies{$tail})
				{
				$tails_nonspecies{$tail}=0;
				}
			print OUT_MODS_SPECIES"$tail\t$tails_species{$tail}\n";
			print OUT_MODS_NONSPECIES"$tail\t$tails_nonspecies{$tail}\n";
			}

		print OUT_MODS_SPECIES"\nInternal modifications (?->[ATGCN] = mapped sequence exceeds precursor sequence)\n";
		print OUT_MODS_NONSPECIES"\nInternal modifications (?->[ATGCN] = mapped sequence exceeds precursor sequence)\n";
		foreach$mod(sort{$count_internal_mods_species{$b}<=>$count_internal_mods_species{$a}}keys%count_internal_mods_species)
			{
			if($mod!~/\d/){print OUT_MODS_SPECIES"$mod\t$count_internal_mods_species{$mod}\n";}
			}
		foreach$mod(sort{$count_internal_mods_nonspecies{$b}<=>$count_internal_mods_nonspecies{$a}}keys%count_internal_mods_nonspecies)
			{
			if($mod!~/\d/){print OUT_MODS_NONSPECIES"$mod\t$count_internal_mods_nonspecies{$mod}\n";}
			}
		print OUT_MODS_SPECIES"\nPositions were internal modifications occur\n";
		print OUT_MODS_NONSPECIES"\nPositions were internal modifications occur\n";
		foreach$mod(1..100)
			{
			if($count_internal_mods_species{$mod}>0){print OUT_MODS_SPECIES"$mod\t$count_internal_mods_species{$mod}\n";}
			}
		foreach$mod(1..100)
			{
			if($count_internal_mods_species{$mod}>0){print OUT_MODS_NONSPECIES"$mod\t$count_internal_mods_nonspecies{$mod}\n";}
			}

		print OUT_MODS_SPECIES"\nModifications per position\n";
		print OUT_MODS_NONSPECIES"\nModifications per position\n";
		foreach$mod(sort{$count_internal_mods_species{$b}<=>$count_internal_mods_species{$a}}keys%count_internal_mods_species)
			{
			if($mod!~/\d/){print OUT_MODS_SPECIES"\t$mod";}
			}
		foreach$pos(1..100)
			{
			if($count_internal_mods_species{$pos}>0)
				{
				print OUT_MODS_SPECIES"\n$pos";
				foreach$mod(sort{$count_internal_mods_species{$b}<=>$count_internal_mods_species{$a}}keys%count_internal_mods_species)
					{
					next if($mod=~/\d/);
					if(!$mods_per_pos_species{$pos}{$mod})
						{
						$mods_per_pos_species{$pos}{$mod}=0;
						}
					print OUT_MODS_SPECIES"\t$mods_per_pos_species{$pos}{$mod}";
					}
				}
			}
		foreach$mod(sort{$count_internal_mods_nonspecies{$b}<=>$count_internal_mods_nonspecies{$a}}keys%count_internal_mods_nonspecies)
			{
			if($mod!~/\d/){print OUT_MODS_NONSPECIES"\t$mod";}
			}
		foreach$pos(1..100)
			{
			if($count_internal_mods_nonspecies{$pos}>0)
				{
				print OUT_MODS_NONSPECIES"\n$pos";
				foreach$mod(sort{$count_internal_mods_nonspecies{$b}<=>$count_internal_mods_nonspecies{$a}}keys%count_internal_mods_nonspecies)
					{
					next if($mod=~/\d/);
					if(!$mods_per_pos_nonspecies{$pos}{$mod})
						{
						$mods_per_pos_nonspecies{$pos}{$mod}=0;
						}
					print OUT_MODS_NONSPECIES"\t$mods_per_pos_nonspecies{$pos}{$mod}";
					}
				}
			}
		close OUT_MODS_SPECIES;
		close OUT_MODS_NONSPECIES;

		# make miRNA tables
		open(OUT_TABLE_SPECIES,">$dump_folder/$outfile_prefix.miR-table_$species{$species}.txt")||die print"\nERROR! Unable to create output file $dump_folder/$outfile_prefix.miR-table_$species{$species}.txt.\n$!\n\n";
		open(OUT_TABLE_NONSPECIES,">$dump_folder/$outfile_prefix.miR-table_non-$species{$species}.txt")||die print"\nERROR! Unable to create output file $dump_folder/$outfile_prefix.miR-table_non-$species{$species}.txt.\n$!\n\n";
		print OUT_TABLE_SPECIES"miR-name\tsequence\ttotal_reads";
		print OUT_TABLE_NONSPECIES"miR-name\tsequence\ttotal_reads";
		foreach$n(0..$max_miRtail_length)
			{
			print OUT_TABLE_SPECIES"\t+N$n-3'";
			print OUT_TABLE_NONSPECIES"\t+N$n-3'";
			}
		foreach$tail(@combs)
			{
			print OUT_TABLE_SPECIES"\t$tail-tailed_reads";
			print OUT_TABLE_NONSPECIES"\t$tail-tailed_reads";
			}
		print OUT_TABLE_SPECIES"\n";
		print OUT_TABLE_NONSPECIES"\n";

		foreach$seq(sort{$a cmp $b}keys%miR_table_out_species)
			{
			# get miRNA name
			if($miRBase_species{$seq})
				{
				$miR_name=$miRBase_species{$seq};
				}
			else
				{
				$miR_name=$targets_per_miRspecies{$seq};
				}

			# get total miRNA counts
			$miR_counts=0;
			foreach$i(0..$max_miRtail_length)
				{
				$miR_counts+=$miR_table_out_species{$seq}{$i};
				}

			print OUT_TABLE_SPECIES"$miR_name\t$seq\t$miR_counts\t";
			foreach$n(0..$max_miRtail_length)
				{
				if(!$miR_table_out_species{$seq}{$n})
					{
					$miR_table_out_species{$seq}{$n}=0;
					}
				print OUT_TABLE_SPECIES"$miR_table_out_species{$seq}{$n}\t";
				}
			foreach$tail(@combs)
				{
				if(!$tails_per_miR_species{$seq}{$tail})
					{
					$tails_per_miR_species{$seq}{$tail}=0;
					}
				print OUT_TABLE_SPECIES"$tails_per_miR_species{$seq}{$tail}\t";
				}
			print OUT_TABLE_SPECIES"\n";
			}

		foreach$seq(sort{$a cmp $b}keys%miR_table_out_nonspecies)
			{
			# get miRNA name
			if($miRBase_nonspecies{$seq})
				{
				$miR_name=$miRBase_nonspecies{$seq};
				}
			else
				{
				$miR_name=$targets_per_miRnonspecies{$seq};
				}

			# get total miRNA counts
			$miR_counts=0;
			foreach$i(0..$max_miRtail_length)
				{
				$miR_counts+=$miR_table_out_nonspecies{$seq}{$i};
				}

			print OUT_TABLE_NONSPECIES"$miR_name\t$seq\t$miR_counts\t";
			foreach$n(0..$max_miRtail_length)
				{
				if(!$miR_table_out_nonspecies{$seq}{$n})
					{
					$miR_table_out_nonspecies{$seq}{$n}=0;
					}
				print OUT_TABLE_NONSPECIES"$miR_table_out_nonspecies{$seq}{$n}\t";
				}
			foreach$tail(@combs)
				{
				if(!$tails_per_miR_nonspecies{$seq}{$tail})
					{
					$tails_per_miR_nonspecies{$seq}{$tail}=0;
					}
				print OUT_TABLE_NONSPECIES"$tails_per_miR_nonspecies{$seq}{$tail}\t";
				}
			print OUT_TABLE_NONSPECIES"\n";
			}
		close OUT_TABLE_SPECIES;
		close OUT_TABLE_NONSPECIES;
		}
	else # make a non-miR file to search for other ncRNA classes
		{
		if(!$silent){print" \nSkip miRNA annotation. Prepare file for ncRNA annotation...";}
		$i_miRspecies=0;
		$i_miRnonspecies=0;
		$nonid_miRspecies=0;
		$nonid_miRnonspecies=0;
		copy($input,"$dump_folder/$outfile_prefix.non-miR.fas")||die print"\nERROR! Unable to create input file for ncRNA search.\n$!\n\n";
		}

	# search ncRNAs other than miRNAs or mRNA fragments (if non-miR.fas is not empty)
	if(!$skip_cDNA||!$skip_ncRNA)
		{
		if($no_nonmiRs==0)
			{
			$map_to_what="";
			# add cDNA sequences to ncRNA file
			if(!$skip_cDNA&&!$skip_ncRNA)
				{
				$map_to_what="ncRNA and cDNA";
				open(SEQMAP_REF,">>$dump_folder\/ncRNA.fas")||die print"ERROR! Unable to open $dump_folder/ncRNA.fas to add cDNA sequences.\n$!\n\n";
				open(CDNA,"$dump_folder\/cDNA.fas")||die print"ERROR! Unable to open $dump_folder/cDNA.fas to add cDNA sequences.\n$!\n\n";
				$total_cDNA_reference_bp=0;
				while(<CDNA>)
					{
					print SEQMAP_REF$_;
					if($_!~/^>/)
						{
						$_=~s/\s*$//;
						$total_cDNA_reference_bp+=length$_;
						}
					}
				close SEQMAP_REF;
				close CDNA;
				$reference_file="$dump_folder\/ncRNA.fas";
				}
			# map to ncRNA only
			elsif(!$skip_ncRNA)
				{
				$map_to_what="ncRNA";
				$reference_file="$dump_folder\/ncRNA.fas";
				}
			# map to cDNA only
			elsif(!$skip_cDNA)
				{
				$map_to_what="cDNA";
				$reference_file="$dump_folder\/cDNA.fas";
				}

			# get lengths of tRNAs to identify 3' fragments, optionally add CCA to 3' end and save added sequences in a new file: .CCAcheck
			open(NCRNA,"$dump_folder/ncRNA.fas")||die print"\nERROR! Unable to open $dump_folder/ncRNA.fas.\n$!\n\n";
			if(!$noCCAcheck)
				{
				open(CCACHECK,">$dump_folder/ncRNA.fas.CCAcheck")||die print"\nERROR! Unable to create file $dump_folder/ncRNA.fas.CCAcheck.\n$!\n\n";
				}
			%tRNA_length=();
			while(<NCRNA>)
				{
				$_=~s/\s*$//;
				if($_=~s/^>//)
					{
					if($_=~/tRNA\|/)
						{
						$head=$_;
						# add CCA to 3' terminal end of tRNAs (opt-out)
						if($_!~/tRF-1_/&&$_!~/tRNA-leader_/&&!$noCCAcheck)
							{
							$doCCAcheck=1;
							}
						else
							{
							$doCCAcheck=0;
							}
						}
					else
						{
						$head="X";
						}
					if(!$noCCAcheck)
						{
						print CCACHECK">$_\n";
						}
					}
				elsif($head ne"X")
					{
					#print"\ndatabase_version:\n$_";
					if($_=~/[^C]C$/&&$doCCAcheck==1)
						{
						$_.="CA";
						}
					elsif($_=~/CC$/&&$doCCAcheck==1)
						{
						$_.="A";
						}
					elsif($_!~/CCA$/&&$doCCAcheck==1)
						{
						$_.="CCA";
						}
					$tRNA_length{$head}=length$_;
					print CCACHECK"$_\n";
					#print"\nedited_version:\n$_\n";
					}
				else
					{
					print CCACHECK"$_\n";
					}
				}
			close NCRNA;
			if(!$noCCAcheck)
				{
				close CCACHECK;
				$reference_file="$dump_folder/ncRNA.fas.CCAcheck";
				}

			$total_reference_bp=$total_cDNA_reference_bp+$total_ncRNA_reference_bp;
			if(!$silent){print" OK.\nCompare non-miR sequences with $map_to_what sequences (total reference sequence: $total_reference_bp bp)...";}
			if($^O eq'linux'||$^O eq'darwin')
				{
				qx/.\/seqmap.exe $seqmap_max_mm $dump_folder\/$outfile_prefix.non-miR.fas $reference_file $dump_folder\/SeqMap_ncRNA.temp \/allow_insdel:$seqmap_max_insdel \/output_all_matches \/forward_strand \/available_memory:${memory}000 $less_memory_options_ncR> $dump_folder\/SeqMap_report3.txt/;
				}
			elsif($^O eq'MSWin32')
				{
				system("seqmap.exe $seqmap_max_mm $dump_folder/$outfile_prefix.non-miR.fas $reference_file $dump_folder/SeqMap_ncRNA.temp /allow_insdel:$seqmap_max_insdel /output_all_matches \/forward_strand /available_memory:${memory}000 ${less_memory_options_ncR}2>&1 > $dump_folder/SeqMap_report3.txt");
				}
			else
				{
				print" WARNING! Unknown operating system.";
				#try unix command
				qx/.\/seqmap.exe $seqmap_max_mm $dump_folder\/$outfile_prefix.non-miR.fas $reference_file $dump_folder\/SeqMap_ncRNA.temp \/allow_insdel:$seqmap_max_insdel \/output_all_matches \/forward_strand \/available_memory:${memory}000 $less_memory_options_ncR> $dump_folder\/SeqMap_report3.txt/;
				if(!-e"$dump_folder\/SeqMap_ncRNA.temp")
					{
					# try windows command
					system("seqmap.exe $seqmap_max_mm $dump_folder/$outfile_prefix.non-miR.fas $reference_file $dump_folder/SeqMap_ncRNA.temp /allow_insdel:$seqmap_max_insdel /output_all_matches \/forward_strand /available_memory:${memory}000 ${less_memory_options_ncR}2>&1 > $dump_folder/SeqMap_report3.txt");
					}
				}

			# check SeqMap report
			open(SMR,"$dump_folder/SeqMap_report3.txt");
			while(<SMR>)
				{
				if($_=~/not enough memory/)
					{
					print"\n\nERROR. Not enough memory available for SeqMap. Check $dump_folder/SeqMap_report3.txt for more details. Try less mismatch (-mismatch [n]), less insertions/deletions (-insdel [n]), fewer non-template 3' nucleotides (-tail [n]). Alternatively you may want to use the options -less_memory or -even_less_memory.\n\n";
					exit;
					}
				}
			close SMR;

			if($seqmap_max_mm+$seqmap_max_insdel>0)
				{
				clean_SeqMap_ncRNA();
				open(SEQMAP,"$dump_folder/SeqMap_ncRNA.clean.temp")||die print"\nERROR! Unable to open $dump_folder/SeqMap_ncRNA.clean.temp.\n$!\n\n";
				}
			else
				{
				open(SEQMAP,"$dump_folder/SeqMap_ncRNA.temp")||die print"\nERROR! Unable to open $dump_folder/SeqMap_ncRNA.temp.\n$!\n\n";
				$head=<SEQMAP>;
				}
			%hits_per_seq=();
			while(<SEQMAP>)
				{
				$_=~s/\s*$//;
				@seqmap=split("\t",$_);
				$hits_per_seq{$seqmap[4]}++;
				}
			close SEQMAP;

			$tRNA_half5{"genomic"}=0;
			$tRF5{"genomic"}=0;
			$tRNA_half3{"genomic"}=0;
			$tRF3{"genomic"}=0;
			$tRF3_CCA{"genomic"}=0;
			$tRF1{"genomic"}=0;
			$tRNAleader{"genomic"}=0;
			$misc_tRF{"genomic"}=0;
			$tRNA_half5{"Mt"}=0;
			$tRF5{"Mt"}=0;
			$tRNA_half3{"Mt"}=0;
			$tRF3{"Mt"}=0;
			$tRF3_CCA{"Mt"}=0;
			$tRF1{"Mt"}=0;
			$tRNAleader{"Mt"}=0;
			$misc_tRF{"Mt"}=0;

			if($seqmap_max_mm+$seqmap_max_insdel>0)
				{
				open(SEQMAP,"$dump_folder/SeqMap_ncRNA.clean.temp")||die print"\nERROR! Unable to open $dump_folder/SeqMap_ncRNA.clean.temp.\n$!\n\n";
				}
			else
				{
				open(SEQMAP,"$dump_folder/SeqMap_ncRNA.temp")||die print"\nERROR! Unable to open $dump_folder/SeqMap_ncRNA.temp.\n$!\n\n";
				$head=<SEQMAP>;
				}
			while(<SEQMAP>)
				{
				$_=~s/\s*$//;
				@seqmap=split("\t",$_);

				# save alignment start and end coordinates
				$map_start_pos=$seqmap[1];
				$map_end_pos=($seqmap[1]+length$seqmap[2])-1;
				$alignment_info="($map_start_pos-$map_end_pos)";

				# avoid nested quantifiers in sequence names such as 'trna3197-Undet???'
				$seqmap[0]=~s/\?/X/g;
				$seqmap[0]=~s/\*/x/g;
				$seqmap[0]=~s/\+/plus/g;

				@id=split(/\|/,$seqmap[0]);
				$sorted_sequences{$id[0]}{$seqmap[4]}=$seqmap[3];
				$ncRNA_counts_1{$id[0]}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
				$ncRNA_counts_2{$id[0]}{$id[1]}+=$seqmap[3]/$hits_per_seq{$seqmap[4]}; # hits per transcript table

				# check tRNA derived sequences
				if($seqmap[0]=~/^tRNA\|/||$seqmap[0]=~/^Mt_tRNA\|/)
					{
					$origin="genomic";
					if($seqmap[0]=~/^Mt_tRNA\|/)
						{
						$origin="Mt";
						}
					$source_tRNA=$seqmap[0];
					$source_tRNA=~s/^[^\|]+\|//;

					# tRF-1 (derived from tRNA trailer)
					if($seqmap[0]=~/tRNA\|tRF-1_/)
						{
						$source_tRNA=~s/^tRF-1_\[*\d*\]*_*//;
						$tRF1{$origin}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
						$tRF_seqs{"tRF-1"}{$origin}{$seqmap[4]}=$seqmap[3];
						$tRF_data_table{$source_tRNA}{"tRF-1"}{"total"}+=$seqmap[3];
						$tRF_data_table{$source_tRNA}{"tRF-1"}{"normalized"}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
						if($global_annotation_matrix{$seqmap[4]}{$seqmap[3]}!~/tRF-1 from $source_tRNA $alignment_info/){$global_annotation_matrix{$seqmap[4]}{$seqmap[3]}.="tRF-1 from $source_tRNA $alignment_info\t";}
						}
					# tRNA leader sequence
					elsif($seqmap[0]=~/tRNA\|tRNA-leader_/)
						{
						$source_tRNA=~s/^tRNA-leader_\[*\d*\]*_*//;
						$tRNAleader{$origin}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
						$tRF_seqs{"tRNA-leader"}{$origin}{$seqmap[4]}=$seqmap[3];
						$tRF_data_table{$source_tRNA}{"tRNA-leader"}{"total"}+=$seqmap[3];
						$tRF_data_table{$source_tRNA}{"tRNA-leader"}{"normalized"}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
						if($global_annotation_matrix{$seqmap[4]}{$seqmap[3]}!~/tRF-leader from $source_tRNA $alignment_info/){$global_annotation_matrix{$seqmap[4]}{$seqmap[3]}.="tRF-leader from $source_tRNA $alignment_info\t";}
						}
					# 5' fragment
					elsif($seqmap[1]==1+$tRNA_max_dist_from_end)
						{
						if(length$seqmap[4]>=$tRNA_half_min_size)
							{
							$tRNA_half5{$origin}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
							$tRF_seqs{"5p-tR-halves"}{$origin}{$seqmap[4]}=$seqmap[3];
							$tRF_data_table{$source_tRNA}{"5p-tR-halves"}{"total"}+=$seqmap[3];
							$tRF_data_table{$source_tRNA}{"5p-tR-halves"}{"normalized"}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
							if($global_annotation_matrix{$seqmap[4]}{$seqmap[3]}!~/5p-tR-half from $source_tRNA $alignment_info/){$global_annotation_matrix{$seqmap[4]}{$seqmap[3]}.="5p-tR-half from $source_tRNA $alignment_info\t";}
							}
						else
							{
							$tRF5{$origin}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
							$tRF_seqs{"5p-tRFs"}{$origin}{$seqmap[4]}=$seqmap[3];
							$tRF_data_table{$source_tRNA}{"5p-tRFs"}{"total"}+=$seqmap[3];
							$tRF_data_table{$source_tRNA}{"5p-tRFs"}{"normalized"}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
							if($global_annotation_matrix{$seqmap[4]}{$seqmap[3]}!~/5p-tRF from $source_tRNA $alignment_info/){$global_annotation_matrix{$seqmap[4]}{$seqmap[3]}.="5p-tRF from $source_tRNA $alignment_info\t";}
							}
						}
					# 3' fragment (not necessarily the very 3' end, tRNA may have up to 3bp 3' overhang in the alignment [-3])
					elsif($seqmap[1]+(length$seqmap[4])-1>=($tRNA_length{$seqmap[0]})-(3+$tRNA_max_dist_from_end))
						{
						if(length$seqmap[4]>=$tRNA_half_min_size)
							{
							$tRNA_half3{$origin}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
							$tRF_seqs{"3p-tR-halves"}{$origin}{$seqmap[4]}=$seqmap[3];
							$tRF_data_table{$source_tRNA}{"3p-tR-halves"}{"total"}+=$seqmap[3];
							$tRF_data_table{$source_tRNA}{"3p-tR-halves"}{"normalized"}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
							if($global_annotation_matrix{$seqmap[4]}{$seqmap[3]}!~/3p-tR-half from $source_tRNA $alignment_info/){$global_annotation_matrix{$seqmap[4]}{$seqmap[3]}.="3p-tR-half from $source_tRNA $alignment_info\t";}
							}
						else
							{
							# CCA fragments have to match the very 3' end of the tRNA
							if($seqmap[4]=~/CCA$/i&&$seqmap[1]+(length$seqmap[4])-1==$tRNA_length{$seqmap[0]})
								{
								CCA();
								sub CCA
									{
									$tRF3_CCA{$origin}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
									$tRF_seqs{"3p-CCA-tRFs"}{$origin}{$seqmap[4]}=$seqmap[3];
									$tRF_data_table{$source_tRNA}{"3p-CCA-tRFs"}{"total"}+=$seqmap[3];
									$tRF_data_table{$source_tRNA}{"3p-CCA-tRFs"}{"normalized"}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
									if($global_annotation_matrix{$seqmap[4]}{$seqmap[3]}!~/3p-CCA-tRF from $source_tRNA $alignment_info/){$global_annotation_matrix{$seqmap[4]}{$seqmap[3]}.="3p-CCA-tRF from $source_tRNA $alignment_info\t";}
									}
								}
							elsif($seqmap[4]=~/CC$/i&&$CC_is_CCA&&$seqmap[1]+(length$seqmap[4])==$tRNA_length{$seqmap[0]}) # allows to identify 3p-CCA-tRFs if poly-A tails were trimmed
								{
								CCA();
								}
							else
								{
								$tRF3{$origin}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
								$tRF_seqs{"3p-tRFs"}{$origin}{$seqmap[4]}=$seqmap[3];
								$tRF_data_table{$source_tRNA}{"3p-tRFs"}{"total"}+=$seqmap[3];
								$tRF_data_table{$source_tRNA}{"3p-tRFs"}{"normalized"}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
								if($global_annotation_matrix{$seqmap[4]}{$seqmap[3]}!~/3p-tRF from $source_tRNA $alignment_info/){$global_annotation_matrix{$seqmap[4]}{$seqmap[3]}.="3p-tRF from $source_tRNA $alignment_info\t";}
								}
							}
						}
					else
						{
						$misc_tRF{$origin}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
						$tRF_seqs{"misc-tRFs"}{$origin}{$seqmap[4]}=$seqmap[3];
						$tRF_data_table{$source_tRNA}{"misc-tRFs"}{"total"}+=$seqmap[3];
						$tRF_data_table{$source_tRNA}{"misc-tRFs"}{"normalized"}+=$seqmap[3]/$hits_per_seq{$seqmap[4]};
						if($global_annotation_matrix{$seqmap[4]}{$seqmap[3]}!~/misc-tRF from $source_tRNA $alignment_info/){$global_annotation_matrix{$seqmap[4]}{$seqmap[3]}.="misc-tRF from $source_tRNA $alignment_info\t";}
						}
					}
				else
					{
					if($global_annotation_matrix{$seqmap[4]}{$seqmap[3]}!~/\Q$seqmap[0] $alignment_info/i){$global_annotation_matrix{$seqmap[4]}{$seqmap[3]}.="$seqmap[0] $alignment_info\t";}
					}
				}
			close SEQMAP;
			}
		}

	# create tRF fasta files
	foreach$tRF_type("5p-tR-halves","5p-tRFs","3p-tR-halves","3p-CCA-tRFs","3p-tRFs","tRF-1","tRNA-leader","misc-tRFs")
		{
		foreach$origin("genomic","Mt")
			{
			if(keys%{$tRF_seqs{$tRF_type}{$origin}}>0)
				{
				open(TRF,">$dump_folder/$outfile_prefix.${origin}_$tRF_type.fas")||die print"\nERROR! Unable to create file $dump_folder/$outfile_prefix.${origin}_$tRF_type.fas.\n$!\n\n";
				foreach$seq(sort{$tRF_seqs{$tRF_type}{$origin}{$b}<=>$tRF_seqs{$tRF_type}{$origin}{$a} or $a cmp $b} keys%{$tRF_seqs{$tRF_type}{$origin}})
					{
					print TRF">$tRF_seqs{$tRF_type}{$origin}{$seq}\n$seq\n";
					}
				close TRF;
				$ba_in="$dump_folder/$outfile_prefix.${origin}_$tRF_type.fas";
				$ba_out="$dump_folder/$outfile_prefix.${origin}_$tRF_type.info";
				basic_analysis();
				}
			}
		}

	# create tRF table
	if(!$skip_ncRNA)
		{
		open(TRF_TABLE,">$dump_folder/${outfile_prefix}.tRF-table.txt")||die print"\nERROR! Unable to create file $dump_folder/$outfile_prefix.${origin}_tRF-table.txt.\n$!\n\n";
		print TRF_TABLE"NOTE: SEQUENCE READS MAY MAP TO MORE THAN ONE REFERENCE SEQUENCE.\nFRACTIONATED READ COUNTS ACCOUNT FOR MULTIPLE HITS OF ONE SEQUENCE.\n\nsource_tRNA\t5p-tR-halves (fractionated)\t5p-tR-halves (absolute)\t5p-tRFs (fractionated)\t5p-tRFs (absolute)\t3p-tR-halves (fractionated)\t3p-tR-halves (absolute)\t3p-CCA-tRFs (fractionated)\t3p-CCA-tRFs (absolute)\t3p-tRFs (fractionated)\t3p-tRFs (absolute)\ttRF-1 (fractionated)\ttRF-1 (absolute)\ttRNA-leader (fractionated)\ttRNA-leader (absolute)\tmisc-tRFs (fractionated)\tmisc-tRFs (absolute)";
		foreach$source_tRNA(sort{$a cmp $b}keys%tRF_data_table)
			{
			if(!$tRF_data_table{$source_tRNA}{"5p-tR-halves"}{"total"}){$tRF_data_table{$source_tRNA}{"5p-tR-halves"}{"total"}=0;}
			if(!$tRF_data_table{$source_tRNA}{"5p-tR-halves"}{"normalized"}){$tRF_data_table{$source_tRNA}{"5p-tR-halves"}{"normalized"}=0;}
			if(!$tRF_data_table{$source_tRNA}{"5p-tRFs"}{"total"}){$tRF_data_table{$source_tRNA}{"5p-tRFs"}{"total"}=0;}
			if(!$tRF_data_table{$source_tRNA}{"5p-tRFs"}{"normalized"}){$tRF_data_table{$source_tRNA}{"5p-tRFs"}{"normalized"}=0;}
			if(!$tRF_data_table{$source_tRNA}{"3p-tR-halves"}{"total"}){$tRF_data_table{$source_tRNA}{"3p-tR-halves"}{"total"}=0;}
			if(!$tRF_data_table{$source_tRNA}{"3p-tR-halves"}{"normalized"}){$tRF_data_table{$source_tRNA}{"3p-tR-halves"}{"normalized"}=0;}
			if(!$tRF_data_table{$source_tRNA}{"3p-CCA-tRFs"}{"total"}){$tRF_data_table{$source_tRNA}{"3p-CCA-tRFs"}{"total"}=0;}
			if(!$tRF_data_table{$source_tRNA}{"3p-CCA-tRFs"}{"normalized"}){$tRF_data_table{$source_tRNA}{"3p-CCA-tRFs"}{"normalized"}=0;}
			if(!$tRF_data_table{$source_tRNA}{"3p-tRFs"}{"total"}){$tRF_data_table{$source_tRNA}{"3p-tRFs"}{"total"}=0;}
			if(!$tRF_data_table{$source_tRNA}{"3p-tRFs"}{"normalized"}){$tRF_data_table{$source_tRNA}{"3p-tRFs"}{"normalized"}=0;}
			if(!$tRF_data_table{$source_tRNA}{"tRF-1"}{"total"}){$tRF_data_table{$source_tRNA}{"tRF-1"}{"total"}=0;}
			if(!$tRF_data_table{$source_tRNA}{"tRF-1"}{"normalized"}){$tRF_data_table{$source_tRNA}{"tRF-1"}{"normalized"}=0;}
			if(!$tRF_data_table{$source_tRNA}{"tRNA-leader"}{"total"}){$tRF_data_table{$source_tRNA}{"tRNA-leader"}{"total"}=0;}
			if(!$tRF_data_table{$source_tRNA}{"tRNA-leader"}{"normalized"}){$tRF_data_table{$source_tRNA}{"tRNA-leader"}{"normalized"}=0;}
			if(!$tRF_data_table{$source_tRNA}{"misc-tRFs"}{"total"}){$tRF_data_table{$source_tRNA}{"misc-tRFs"}{"total"}=0;}
			if(!$tRF_data_table{$source_tRNA}{"misc-tRFs"}{"normalized"}){$tRF_data_table{$source_tRNA}{"misc-tRFs"}{"normalized"}=0;}
			print TRF_TABLE"\n$source_tRNA\t".$tRF_data_table{$source_tRNA}{"5p-tR-halves"}{"normalized"}."\t".$tRF_data_table{$source_tRNA}{"5p-tR-halves"}{"total"};
			print TRF_TABLE"\t".$tRF_data_table{$source_tRNA}{"5p-tRFs"}{"normalized"}."\t".$tRF_data_table{$source_tRNA}{"5p-tRFs"}{"total"};
			print TRF_TABLE"\t".$tRF_data_table{$source_tRNA}{"3p-tR-halves"}{"normalized"}."\t".$tRF_data_table{$source_tRNA}{"3p-tR-halves"}{"total"};
			print TRF_TABLE"\t".$tRF_data_table{$source_tRNA}{"3p-CCA-tRFs"}{"normalized"}."\t".$tRF_data_table{$source_tRNA}{"3p-CCA-tRFs"}{"total"};
			print TRF_TABLE"\t".$tRF_data_table{$source_tRNA}{"3p-tRFs"}{"normalized"}."\t".$tRF_data_table{$source_tRNA}{"3p-tRFs"}{"total"};
			print TRF_TABLE"\t".$tRF_data_table{$source_tRNA}{"tRF-1"}{"normalized"}."\t".$tRF_data_table{$source_tRNA}{"tRF-1"}{"total"};
			print TRF_TABLE"\t".$tRF_data_table{$source_tRNA}{"tRNA-leader"}{"normalized"}."\t".$tRF_data_table{$source_tRNA}{"tRNA-leader"}{"total"};
			print TRF_TABLE"\t".$tRF_data_table{$source_tRNA}{"misc-tRFs"}{"normalized"}."\t".$tRF_data_table{$source_tRNA}{"misc-tRFs"}{"total"};
			}
		close TRF_TABLE;
		}

	# create ncRNA/cDNA fasta files, ncRNA/cDNA count table and non-annotated file
	open(NCRNA_TABLE,">$dump_folder/$outfile_prefix.annotation_summary.txt")||die print"\nERROR! Unable to create file $dump_folder/$outfile_prefix.annotation_summary.txt.\n$!\n\n";
	if(!$skip_dust)
		{
		print NCRNA_TABLE"low_complexity\t$simple_reads\n";
		}
	if(!$ncRNA_counts_1{"tRNA"}){$ncRNA_counts_1{"tRNA"}=0;}
	if(!$ncRNA_counts_1{"Mt_tRNA"}){$ncRNA_counts_1{"Mt_tRNA"}=0;}
	if(!$ncRNA_counts_1{"rRNA"}){$ncRNA_counts_1{"rRNA"}=0;}
	if(!$ncRNA_counts_1{"Mt_rRNA"}){$ncRNA_counts_1{"Mt_rRNA"}=0;}
	$total_miR_reads=$i_miRspecies+$i_miRnonspecies;
	$total_tRNA_reads=$ncRNA_counts_1{"tRNA"}+$ncRNA_counts_1{"Mt_tRNA"};
	$total_rRNA_reads=$ncRNA_counts_1{"rRNA"}+$ncRNA_counts_1{"Mt_rRNA"};
	print NCRNA_TABLE"miRNA\t$total_miR_reads\n   miRNA:$species\t$i_miRspecies\n   miRNA:other\t$i_miRnonspecies\n";
	print NCRNA_TABLE"rRNA\t$total_rRNA_reads\n   genomic_rRNA\t$ncRNA_counts_1{rRNA}\n   Mt_rRNA\t$ncRNA_counts_1{Mt_rRNA}\n";
	print NCRNA_TABLE"tRNA\t$total_tRNA_reads\n   genomic_tRNA\t$ncRNA_counts_1{tRNA}\n      5'tR-halves\t$tRNA_half5{genomic}\n      5'tRFs\t$tRF5{genomic}\n      3'tR-halves\t$tRNA_half3{genomic}\n      3'tRFs\t$tRF3{genomic}\n      3'CCA-tRFs\t$tRF3_CCA{genomic}\n      tRF-1\t$tRF1{genomic}\n      tRNA-leader\t$tRNAleader{genomic}\n      misc-tRFs\t$misc_tRF{genomic}\n   Mt_tRNA\t$ncRNA_counts_1{Mt_tRNA}\n      5'tR-halves\t$tRNA_half5{Mt}\n      5'tRFs\t$tRF5{Mt}\n      3'tR-halves\t$tRNA_half3{Mt}\n      3'tRFs\t$tRF3{Mt}\n      3'CCA-tRFs\t$tRF3_CCA{Mt}\n      tRF-1\t$tRF1{Mt}\n      tRNA-leader\t$tRNAleader{Mt}\n      misc-tRFs\t$misc_tRF{Mt}\n";

	my%ncRNA_annotated;
	foreach$biotype(keys%sorted_sequences)
		{
		if($biotype ne"tRNA"&&$biotype ne"Mt_tRNA"&&$biotype ne"rRNA"&&$biotype ne"Mt_rRNA")
			{
			print NCRNA_TABLE"$biotype\t$ncRNA_counts_1{$biotype}\n";
			}
		$biotype_filename=$biotype;
		$biotype_filename=~s/[\\\/\*\.\"\[\]:;\|=\,]/_/g;
		open(NCRNA_OUT,">$dump_folder/$outfile_prefix.$biotype_filename.fas")||die print"\nERROR! Unable to create file $dump_folder/$outfile_prefix.$biotype_filename.fas.\n$!\n\n";
		foreach$seq(sort{$sorted_sequences{$biotype}{$b}<=>$sorted_sequences{$biotype}{$a} or $a cmp $b}keys%{$sorted_sequences{$biotype}})
			{
			$ncRNA_annotated{$seq}=1;
			print NCRNA_OUT">$sorted_sequences{$biotype}{$seq}\n$seq\n";
			}
		close NCRNA_OUT;
		$ba_in="$dump_folder/$outfile_prefix.$biotype_filename.fas";
		$ba_out="$dump_folder/$outfile_prefix.$biotype_filename.info";
		basic_analysis();
		}

	open(NO_ANNOTATION,">$dump_folder/$outfile_prefix.no-annotation.fas")||die print"\nERROR! Unable to create output file $dump_folder/$outfile_prefix.no-annotation.fas.\n$!\n\n";
	open(INPUT,$input)||die print"\nERROR! Unable to open input file $input.\n$!\n\n";
	$no_annotation=0;
	my%no_annotation_seqs;
	while(<INPUT>)
		{
		$_=~s/\s*$//;
		if($_=~s/^>//)
			{
			$reads=$_;
			}
		elsif(!$ncRNA_annotated{$_}&&!$output_miR_species{$_}&&!$output_miR_nonspecies{$_})
			{
			$no_annotation+=$reads;
			$no_annotation_seqs{$_}=$reads;
			}
		}
	close INPUT;
	foreach(sort{$no_annotation_seqs{$b}<=>$no_annotation_seqs{$a}}keys%no_annotation_seqs)
		{
		print NO_ANNOTATION">$no_annotation_seqs{$_}\n$_\n";
		}
	close NO_ANNOTATION;
	$ba_in="$dump_folder/$outfile_prefix.no-annotation.fas";
	$ba_out="$dump_folder/$outfile_prefix.no-annotation.info";
	basic_analysis();

	print NCRNA_TABLE"no annotation\t$no_annotation\n";
	# NCRNA_TABLE is finished when piRNA cluster matching reads are added
	if(!$silent){print" OK.";}

	# map reads without annotation to piRNA cluster sequences (if available and if $no_annotation!=0)
	if(-e"$dump_folder/piRclDB"&&!$skip_piR&&$no_annotation!=0)
		{
		if(!$silent){print"\nMap sequences without annotation to $species{$species} piRNA cluster sequences...";}
		if($^O eq'linux'||$^O eq'darwin')
			{
			qx/.\/seqmap.exe $max_miRtail_length $dump_folder\/$outfile_prefix.no-annotation.fas $dump_folder\/piRclDB $dump_folder\/SeqMap_piR.temp \/output_all_matches \/available_memory:${memory}000 $less_memory_options_ncR> $dump_folder\/SeqMap_report4.txt/;
			}
		elsif($^O eq'MSWin32')
			{
			system("seqmap.exe $max_miRtail_length $dump_folder/$outfile_prefix.no-annotation.fas $dump_folder/piRclDB $dump_folder/SeqMap_piR.temp /output_all_matches /available_memory:${memory}000 ${less_memory_options_ncR}2>&1 > $dump_folder/SeqMap_report4.txt");
			}
		else
			{
			if(!$silent){print" WARNING! Unknown operating system.";}
			# try unix command
			qx/.\/seqmap.exe $max_miRtail_length $dump_folder\/$outfile_prefix.no-annotation.fas piRclDB $dump_folder\/SeqMap_piR.temp \/output_all_matches \/available_memory:${memory}000 $less_memory_options_ncR> $dump_folder\/SeqMap_report4.txt/;
			if(!-e"$dump_folder/SeqMap_species.temp")
				{
				# try windows command
				system("seqmap.exe $max_miRtail_length $dump_folder/$outfile_prefix.no-annotation.fas piRclDB $dump_folder/SeqMap_piR.temp /output_all_matches /available_memory:${memory}000 ${less_memory_options_ncR}2>&1 > $dump_folder/SeqMap_report4.txt");
				}
			}

		# check SeqMap report
		open(SMR,"$dump_folder/SeqMap_report4.txt");
		while(<SMR>)
			{
			if($_=~/not enough memory/)
				{
				print"\n\nERROR. Not enough memory available for SeqMap. Check $dump_folder/SeqMap_report4.txt for more details. Try less mismatch (-mismatch [n]), less insertions/deletions (-insdel [n]), fewer non-template 3' nucleotides (-tail [n]). Alternatively you may want to use the options -less_memory or -even_less_memory.\n\n";
				exit;
				}
			}
		close SMR;

		# clean up SeqMap output (same as for miR)
		$file_id="piR";
		clean_SeqMap_miRNA();

		my%hits_per_piR;
		open(SEQMAP,"$dump_folder/SeqMap_piR.clean-2.temp")||die print"\nERROR! Unable to open $dump_folder/SeqMap_piR.clean-2.temp.\n$!\n\n";
		while(<SEQMAP>)
			{
			$_=~s/\s*$//;
			@seqmap=split("\t",$_);
			$hits_per_piR{$seqmap[4]}++;
			}
		close SEQMAP;

		open(SEQMAP,"$dump_folder/SeqMap_piR.clean-2.temp")||die print"\nERROR! Unable to open $dump_folder/SeqMap_piR.clean-2.temp.\n$!\n\n";
		my%pi_tails;
		my%piRNA_candidates;
		my%piRNA_header;
		while(<SEQMAP>)
			{
			$_=~s/\s*$//;
			@seqmap=split("\t",$_);

			$parsed_loc=$seqmap[0];
			$parsed_loc=~s/ /_/g;
			if($global_annotation_matrix{$seqmap[4]}{$seqmap[3]}!~/piR-cluster:$parsed_loc/){$global_annotation_matrix{$seqmap[4]}{$seqmap[3]}.="piR-cluster:$parsed_loc\t";}

			# 3' non-template hit to piRNA cluster
			if($seqmap[5]>0)
				{
				foreach$p((length$seqmap[2])-$max_miRtail_length..(length$seqmap[2])-1)
					{
					if(substr($seqmap[2],$p,1) ne substr($seqmap[4],$p,1))
						{
						$tail=substr($seqmap[4],$p);
						$pi_tails{$tail}+=$seqmap[3]/$hits_per_piR{$seqmap[4]};
						last;
						}
					}
				}
			$piRNA_candidates{$seqmap[4]}=$seqmap[3];
			$piRNA_header{$seqmap[4]}.="$seqmap[0]|";
			}
		close SEQMAP;

		# make piRNA candidates tail file
		open(OUT_PITAILS,">$dump_folder/$outfile_prefix.piRcandidates-tails_$species{$species}.txt")||die print"\nERROR! Unable to create output file $dump_folder/$outfile_prefix.piRcandidates-tails_$species{$species}.txt.\n$!\n\n";
		foreach$tail(@combs)
			{
			if(!$pi_tails{$tail})
				{
				$pi_tails{$tail}=0;
				}
			print OUT_PITAILS"$tail\t$pi_tails{$tail}\n";
			}
		close OUT_PITAILS;

		# make piRNA candidates fasta file
		open(PIR_CANDIDATES,">$dump_folder/$outfile_prefix.piRcandidates.fas")||die print"\nERROR! Unable to create output file $dump_folder/$outfile_prefix.piRcandidates.fas.\n$!\n\n";
		$piRc=0;
		foreach$piR(sort{$piRNA_candidates{$b}<=>$piRNA_candidates{$a}}keys%piRNA_candidates)
			{
			$piRc+=$piRNA_candidates{$piR};
			$piRNA_header{$piR}=~s/\|$//;
			print PIR_CANDIDATES">$piRNA_candidates{$piR}:$piRNA_header{$piR}\n$piR\n";
			}
		close PIR_CANDIDATES;
		$ba_in="$dump_folder/$outfile_prefix.piRcandidates.fas";
		$ba_out="$dump_folder/$outfile_prefix.piRcandidates.info";
		basic_analysis();
		print NCRNA_TABLE"   mapped to piRNA producing loci\t$piRc\n";
		print" done.";
		}

	# search for phased RNAs
	if(@phasi>0)
		{
		if(!$silent){print"\nSearch for phased RNAs in $input_map:";}
		if($na_for_phasi)
			{
			$info_for_phasi_out="This is a subset of mapped reads without annotation.";
			}
		else
			{
			$info_for_phasi_out="This is a subset of all mapped reads.";
			}
		reformat_input_map();
		sub reformat_input_map
			{
			$lines_in_phasi_map=0;
			# create new input map file (and save reads and hits per sequence) if input map is not ELAND3 from collapsed data
			if($format eq'SAM')
				{
				if(!$silent){print"\n -check input map file...";}
				%lines_in_map=();
				open(INPUT_MAP,$input_map)||die print"\nERROR! Unable to open input map file $input_map.\n$!\n\n";
				while(<INPUT_MAP>)
					{
					next if($_=~/^@/); # skip SAM header lines
					@sam=split("\t",$_);
					next if ($sam_mapped{$sam[1]}==0||$sam[3]==0); # skip unmapped reads

					# reverse complement if sequence was mapped to the minus strand (SAM gives sequence of the locus, not of the mapped sequence)
					if($sam_strand{$sam[1]}eq'-')
						{
						$sam[9]=reverse$sam[9];
						$sam[9]=~tr/ATGC/TACG/;
						}

					#print"\n$sam[9]:$input_map_seq{$sam[9]}";
					$lines_in_map{$sam[2]}{$sam[3]}{$input_map_seq{$sam[9]}}{$sam[9]}=$sam_strand{$sam[1]};
					}
				close INPUT_MAP;
				create_phasi_map();
				}
			elsif($format eq'ELAND3'&&$all_identifiers_are_numeric==0)
				{
				if(!$silent){print"\n -check input map file...";}
				%lines_in_map=();
				open(INPUT_MAP,$input_map)||die print"\nERROR! Unable to open input map file $input_map.\n$!\n\n";
				while(<INPUT_MAP>)
					{
					next if($_=~/^trans_id/||$_=~/^#/); # skip ELAND3 header lines
					$_=~s/\s*$//;
					@eland3=split("\t",$_);
					$lines_in_map{$eland3[0]}{$eland3[1]}{$input_map_seq{$eland3[4]}}{$eland3[4]}=$eland3[6];
					}
				close INPUT_MAP;
				create_phasi_map();
				}
			else
				{
				if(!$silent){print"\n -check input map file...";}
				%hits_per_seq=();
				open(INPUT_MAP,$input_map);
				while(<INPUT_MAP>)
					{
					next if($_=~/^trans_id/);
					@map=split("\t",$_);
					$hits_per_seq{$map[4]}++;
					$lines_in_phasi_map++;
					}
				close INPUT_MAP;
				if(!$silent){print" OK. No reformat required.";}
				}

			sub create_phasi_map
				{
				if(!$silent){print" OK.\n -reformat input map file...";}
				%hits_per_seq=();
				open(REFORMAT_MAP,">$dump_folder/eland3collapsed.map")||die print"\nERROR! Unable to create sorted map file for phasiRNA detection.\n$!\n\n";
				foreach$chr(sort{$a cmp $b}keys%lines_in_map)
					{
					foreach$pos(sort{$a<=>$b}keys%{$lines_in_map{$chr}})
						{
						foreach$reads(keys%{$lines_in_map{$chr}{$pos}})
							{
							foreach$seq(keys%{$lines_in_map{$chr}{$pos}{$reads}})
								{
								$hits_per_seq{$seq}++;
								print REFORMAT_MAP"$chr\t$pos\t-\t$reads\t$seq\t-\t$lines_in_map{$chr}{$pos}{$reads}{$seq}\n";
								$lines_in_phasi_map++;
								}
							}
						}
					}
				close REFORMAT_MAP;
				undef%lines_in_map;
				$input_map="$dump_folder/eland3collapsed.map";
				if(!$silent){print" OK.";}
				}
			}

		if(!$silent){print"\n -read map file with sliding window (sw size=$phasi_sw_size bp).\n
 0%                                  50%                                  100%
 |------------------------------------|------------------------------------|
 ";}
		open(INPUT_MAP,$input_map);
		$phasi_progress_lines=0;
		$progress=0;
		while(<INPUT_MAP>)
			{
			next if($_=~/^trans_id/);
			$_=~s/\s*$//;
			@map=split("\t",$_);

			# give information on progress
			$phasi_progress_lines++;
			if(!$silent&&$phasi_progress_lines>=$lines_in_phasi_map/75)
				{
				if(!$silent){print".";}
				$phasi_progress_lines=0;
				$progress++;
				}

			# consider only reads with correct length for phasiRNA prediction
			if($phasi{length$map[4]}==1)
				{
				if($no_annotation_seqs{$map[4]}||!$na_for_phasi)# optionally consider only reads without ncRNA annotation
					{
					$coordinate_unified=$map[1];
					if($map[6]eq'-')
						{
						$coordinate_unified=$map[1]+2; # combine + and - strand mapped reads
						}
					$pos_offset=int(((($coordinate_unified/(length$map[4]))-(int($coordinate_unified/(length$map[4]))))*length$map[4])+0.5);

					# each locus belongs to two sliding windows
					$phasiRNA_readcount{$map[0]}{length$map[4]}{$pos_offset}{(int($coordinate_unified/$phasi_sw_size))+1}+=$map[3]/$hits_per_seq{$map[4]};
					$phasiRNA_readcount{$map[0]}{length$map[4]}{$pos_offset}{(int(($coordinate_unified+($phasi_sw_size/2))/$phasi_sw_size))+1}+=$map[3]/$hits_per_seq{$map[4]};
					$phasi_positions{$map[0]}{length$map[4]}{$pos_offset}{(int($coordinate_unified/$phasi_sw_size))+1}{$coordinate_unified}+=$map[3]/$hits_per_seq{$map[4]};
					$phasi_positions{$map[0]}{length$map[4]}{$pos_offset}{(int(($coordinate_unified+($phasi_sw_size/2))/$phasi_sw_size))+1}{$coordinate_unified}+=$map[3]/$hits_per_seq{$map[4]};

					# additionally save read counts from one strand only to exclude loci with suspicious strand bias
					if($map[6]eq'+')
						{
						$phasiRNA_readcount_plus{$map[0]}{length$map[4]}{$pos_offset}{(int($coordinate_unified/$phasi_sw_size))+1}+=$map[3]/$hits_per_seq{$map[4]};
						$phasiRNA_readcount_plus{$map[0]}{length$map[4]}{$pos_offset}{(int(($coordinate_unified+($phasi_sw_size/2))/$phasi_sw_size))+1}+=$map[3]/$hits_per_seq{$map[4]};
						}

					# save that for calculation of p with binomialdistriburion
					$total_readcount_phasilength{$map[0]}{length$map[4]}{(int($coordinate_unified/$phasi_sw_size))+1}+=$map[3]/$hits_per_seq{$map[4]};
					$total_readcount_phasilength{$map[0]}{length$map[4]}{(int(($coordinate_unified+($phasi_sw_size/2))/$phasi_sw_size))+1}+=$map[3]/$hits_per_seq{$map[4]};

					# count total number of sliding windows for Bonferroni correction of multiple testing
					$total_sw_count{$map[0]}{(int($coordinate_unified/$phasi_sw_size))+1}=1;
					}
				}
			$total_counts{$map[0]}{(int($coordinate_unified/500))+1}+=$map[3]/$hits_per_seq{$map[4]};
			$total_counts{$map[0]}{(int(($coordinate_unified+250)/500))+1}+=$map[3]/$hits_per_seq{$map[4]};
			}
		if(!$silent)
			{
			foreach($progress..74)
				{
				if(!$silent){print".";}
				}
			if(!$silent){print"\n\n";}
			}
		close INPUT_MAP;

		# create hash with factorials
		use bignum;
		$val=1;
		$fac{0}=1;
		foreach(1..$max_factorials_stored)
		    {
		    $val=$val*$_;
		    $fac{$_}=$val;
		    }

		# calculate Bonferroni-corrected p-value
		$total_sw_count=0;
		foreach$loc(keys%total_sw_count)
			{
			$total_sw_count+=keys%{$total_sw_count{$loc}};
			}
		$bonferroni_p=$phasi_p/$total_sw_count;
		$bonferroni_p_print=(int(($bonferroni_p*(10**15))+0.5))/(10**15);

		if(!$silent){print"\n\n -check for significant phasing signature.\n   -> Initial p set by user: $phasi_p\n   -> After corr. for multiple testing ($total_sw_count windows): $bonferroni_p_print\n
 0%                                  50%                                  100%
 |------------------------------------|------------------------------------|
 ";}
		# go through sliding windows
		my%getphasi=();
		my%sufficient_k=();
		my%insufficient_k=();
		$number_of_tests=0;
		$phasi_progress_windows=0;
		foreach$location(keys%phasiRNA_readcount)
			{
			foreach$phasi_length(@phasi)
				{
				foreach$frame(1..$phasi_length)
					{
					$number_of_tests+=keys%{$phasiRNA_readcount{$location}{$phasi_length}{$frame}};
					}
				}
			}
		$progress=0;
		foreach$location(keys%phasiRNA_readcount)
			{
			foreach$phasi_length(@phasi)
				{
				$pphased=1/$phasi_length;
				$pnotphased=1-$pphased;
				foreach$frame(1..$phasi_length)
					{
					foreach$sw_id(keys%{$phasiRNA_readcount{$location}{$phasi_length}{$frame}})
						{
						# skip windows with suspicious strand bias if they have only few phased loci
						$bad_strand_bias=0;
						if($phasiRNA_readcount_plus{$location}{$phasi_length}{$frame}{$sw_id}/$phasiRNA_readcount{$location}{$phasi_length}{$frame}{$sw_id}>$max_allowed_strandbias||$phasiRNA_readcount_plus{$location}{$phasi_length}{$frame}{$sw_id}/$phasiRNA_readcount{$location}{$phasi_length}{$frame}{$sw_id}<1-$max_allowed_strandbias)
							{
							$bad_strand_bias=1;
							}

						# optionally apply further thresholds: minimum number of loci, minimum fraction of phased reads in sw
						if(keys%{$phasi_positions{$location}{$phasi_length}{$frame}{$sw_id}}>=$min_phasi_loci&&$phasiRNA_readcount{$location}{$phasi_length}{$frame}{$sw_id}>=$total_counts{$location}{$sw_id}*$min_phasi_fraction)
							{
							# check p from binomialdistribution / round phased reads and all reads to nearest integer
							$k_observed=int($phasiRNA_readcount{$location}{$phasi_length}{$frame}{$sw_id}+0.5);
							$n=int($total_readcount_phasilength{$location}{$phasi_length}{$sw_id}+0.5);
							next if($k_observed<$min_phasi_reads);
							if($n>$max_factorials_stored)
								{
								if($calc_big_factorials&&!$fac{$n}) # calculate huge factorial if it was not calculated before
									{
									$fac_huge=$fac{$max_factorials_stored};
									foreach($max_factorials_stored+1..$n)
										{
										$fac_huge=$fac_huge*$_;
										}
									$fac{$n}=$fac_huge;
									}
								else # scale down if n is bigger than the highest factorial available
									{
									$k_observed=int(($k_observed/($n/$max_factorials_stored))+0.5);
									$n=$max_factorials_stored;
									}
								}

							# skip calculation if significant k for n was calculated before AND no need for calculation of loci in line
							if($sufficient_k{$n}&&$sufficient_k{$n}<=$k_observed&&$min_in_line<2&&$bad_strand_bias==0)
								{
								save_phasi_positions();
								}
							# skip calculation if higher k was found to be insufficient for this n before
							elsif(!$insufficient_k{$n}||$insufficient_k{$n}<$k_observed)
								{
								$k=-1;
								$p=0;
								while(1)
									{
									$k++;
									$p+=($fac{$n}/($fac{$k}*$fac{$n-$k}))*($pphased**$k)*($pnotphased**($n-$k));
									if($p>1-$bonferroni_p&&$k<$n) # last, sufficient (significant) k is $k+1
										{
										$sufficient_k{$n}=$k+1; # safe sufficient number of k for n total reads
										$max_in_line=1;
										$current_in_line=0;
										if($min_in_line>1)
											{
											foreach$pos(sort{$a<=>$b}keys%{$phasi_positions{$location}{$phasi_length}{$frame}{$sw_id}})
												{
												if($current_in_line==0||$prev_pos+$phasi_length==$pos)
													{
													$current_in_line++;
													if($current_in_line>$max_in_line)
														{
														$max_in_line=$current_in_line;
														}
													}
												else
													{
													$current_in_line=0;
													}
												$prev_pos=$pos;
												}
											}
										if($max_in_line>=$min_in_line&&$bad_strand_bias==0)
											{
											save_phasi_positions();
											sub save_phasi_positions
												{
												foreach$pos(sort{$a<=>$b}keys%{$phasi_positions{$location}{$phasi_length}{$frame}{$sw_id}})
													{
													$getphasi{$location}{$phasi_length}{$pos}=$frame;
													}
												}
											}
										elsif($bad_strand_bias==1)
											{
											if($min_in_line>=5||$min_phasi_loci>=25)
												{
												save_phasi_positions();
												}
											}
										last;
										}
									elsif($k==$k_observed) # last with non-significant p
										{
										# safe in-sufficient number of k for n total reads
										if(!$insufficient_k{$n}||$insufficient_k{$n}<$k)
											{
											$insufficient_k{$n}=$k;
											}
										$p_out_test=1-((int(($p*(10**10))+0.5))/(10**10));
										$bonferroni_out_test=(int(($bonferroni_p*(10**10))+0.5))/(10**10);
										last;
										}
									}
								}
							}
						$phasi_progress_windows++;
						if($phasi_progress_windows>=$number_of_tests/75)
							{
							if(!$silent){print".";}
							$progress++;
							$phasi_progress_windows=0;
							}
						}
					}
				}
			}
		if(!$silent)
			{
			foreach($progress..74)
				{
				if(!$silent){print".";}
				}
			if(!$silent){print"\n\n";}
			}

		$phasi_cluster_id=0;
		$prev_phasi_location="";
		$prev_phasi_coordinate=0;
		$prev_phasi_seq="";
		$loci_occupied=0;
		$cluster_reads_total=0;
		$cluster_reads_normalized=0;
		if(keys%getphasi>0)
			{
			open(PHASI_SORTED,">$dump_folder/$outfile_prefix.phasiRNA.sorted.fas")||print"\nERROR! Unable to open output file $dump_folder/$outfile_prefix.phasiRNA.sorted.fas.\n$!\n\n";
			open(PHASI_ALL,">$dump_folder/$outfile_prefix.phasiRNA.fas")||print"\nERROR! Unable to open output file $dump_folder/$outfile_prefix.phasiRNA.sorted.fas.\n$!\n\n";
			open(PHASI_ALIGN,">$dump_folder/$outfile_prefix.phasiRNA.align")||print"\nERROR! Unable to open output file $dump_folder/$outfile_prefix.phasiRNA.align.\n$!\n\n";
			print PHASI_ALIGN"CLUSTERS OF PHASED RNAs.\nGenomic sequence is represented by the center line.\nReads above the center line are aligned to the + strand and stated in 5'-3' orientation.\nReads below the center line are aligned to the - strand and stated in 3'-5' orientation.\nInformation on sequence read counts and genomic hits per sequence are given in the following format at the beginning of each sequence line: [sequence read count/genomic hits]\n\n";
			my%phasiRNAs=();
			$phasiRNA_readcount=0;
			if(!$silent){print"\n\n -collect putative phasiRNAs...";}
			$phasi_align_out_plus="";
			$phasi_align_out_minus="";
			open(INPUT_MAP,$input_map);
			while(<INPUT_MAP>)
				{
				$_=~s/\s*$//;
				@map=split("\t",$_);
				if($map[6]eq'+'&&$getphasi{$map[0]}{length$map[4]}{$map[1]})
					{
					report_phasi_cluster();
					$loci_occupied++;
					$cluster_reads_total+=$map[3];
					$cluster_reads_normalized+=$map[3]/$hits_per_seq{$map[4]};
					$read_info_head=substr("                  $map[3]/$hits_per_seq{$map[4]} ",-20);
					$whitespace=" " x ($map[1]-$current_cluster_start);
					$phasi_align_out_plus="${read_info_head}${whitespace}$map[4]\n".$phasi_align_out_plus;
					$print_frame=$getphasi{$map[0]}{length$map[4]}{$map[1]};
					report_phasi_sequence();
					}
				elsif($map[6]eq'-'&&$getphasi{$map[0]}{length$map[4]}{$map[1]+2})
					{
					report_phasi_cluster();
					$loci_occupied++;
					$cluster_reads_total+=$map[3];
					$cluster_reads_normalized+=$map[3]/$hits_per_seq{$map[4]};
					$read_info_head=substr("                  $map[3]/$hits_per_seq{$map[4]} ",-20);
					$seq3to5=reverse$map[4];
					$whitespace=" " x ($map[1]-$current_cluster_start);
					$phasi_align_out_minus.="\n${read_info_head}${whitespace}$seq3to5";
					$print_frame=$getphasi{$map[0]}{length$map[4]}{($map[1])+2};
					report_phasi_sequence();
					}

				# REPORT THE CLUSTER TO OUTFILE
				sub report_phasi_cluster
					{
					if($map[0]ne$prev_phasi_location||$map[1]-$min_distance_between_clusters>=$prev_phasi_coordinate||$print_frame!=$prev_frame) # new cluster if chromosome/scaffold changes, distance > threshold, different phasing frame
						{
						if($phasi_cluster_id>0)
							{
							$current_cluster_end=$prev_phasi_coordinate+(length$prev_phasi_seq)-1;
							$cluster_size=$current_cluster_end-$current_cluster_start+1;
							$center_line="                    ".">" x ($current_cluster_end-$current_cluster_start+1);
							$center_line.="\n                    "."<" x ($current_cluster_end-$current_cluster_start+1);
							print PHASI_ALIGN"cluster_ID:$phasi_cluster_id\tlocation:$current_cluster_location\tstart:$current_cluster_start\tend:$current_cluster_end\tsize:$cluster_size bp\tnon-identical_sequences:$loci_occupied\treads:$cluster_reads_total\treads_normalized:$cluster_reads_normalized\n${phasi_align_out_plus}${center_line}$phasi_align_out_minus\n\n";
							$loci_occupied=0;
							$cluster_reads_total=0;
							$cluster_reads_normalized=0;
							}
						$phasi_align_out_plus="";
						$phasi_align_out_minus="";
						$phasi_cluster_id++;
						$current_cluster_location=$map[0];
						$current_cluster_start=$map[1];
