										$ppmatrix{$class_seq1}{$pp_length_2}{$pp_length_1}+=(($reads_per_seq{$d[4]}/$hits_per_seq{$d[4]})*($reads_per_seq{$sw[$i][1]}/$hits_per_seq{$sw[$i][1]}));
