		$pp_height=300;
		opendir(DIR,$dump_folder)||die print$!;
		while($file=readdir(DIR))
			{
			if($file=~/\.ppmatrix$/)
				{
				next if($file=~/non-miR/);
				$class=$file;
				$class=~s/^unitas\.//;
				$class=~s/\.ppmatrix$//;
				$class=~s/global/total/;
				next if(!-e"$dump_folder/$storage/pingpong_$class.html"); # do not make a matrix for classes with <1 read pairs (fractionated)
				open(MATRIX,"$dump_folder/$file")||die print$!;
				$head=<MATRIX>;$head=<MATRIX>;$head=<MATRIX>;$head=<MATRIX>;
				$max_value=0;
				while(<MATRIX>)
					{
					$_=~s/\s*$//;
					@values=split("\t",$_);
					foreach$i(1..@values)
						{
						if($values[$i]>$max_value)
							{
							$max_value=$values[$i];
							}
						}
					}
				close MATRIX;
				next if($max_value==0); # no 10 bp overlap
				open(MATRIX,"$dump_folder/$file")||die print$!;
				$head=<MATRIX>;$head=<MATRIX>;$head=<MATRIX>;$head=<MATRIX>;
				$head=~s/\s*$//;
				@head=split("\t",$head);
				open(MATRIX_HTML,">$dump_folder/$storage/pingpong-matrix_$class.html")||die print$!;
				print MATRIX_HTML
		"<html>
		<b>ping-pong matrix</b><br>
		<small>sequence length combinations within ping-pong pairs</small><br>
		<br>
		<table style=\"border-spacing: 1px; font-size: 10px;\";>
		   <tr>
		      <td> </td>";
				foreach$i(1..@head-1) # head of the matrix
					{
					print MATRIX_HTML"\n      <td> <small>$head[$i]</small></td>";
					}
				print MATRIX_HTML"\n   </tr>";

				while(<MATRIX>)
					{
					@values=split("\t",$_);
					print MATRIX_HTML"\n   <tr>\n      <td><small>$values[0]</small></td>";
					foreach$i(1..@values-1)
						{
						$rel_value=((sqrt($values[$i]))/(sqrt($max_value)));
						$red=0;
						$green=0;
						$blue=0;
						if($rel_value<=0.5)
							{
							$blue=(int(($rel_value*255)+0.5))*2;
							}
						else
							{
							$blue=255-((int((($rel_value-0.5)*255)+0.5))*2);
							$red=(int((($rel_value-0.5)*255)+0.5))*2;
							}
						$x="";
						if($values[$i]==$max_value)
							{
							$x="x"
							}
						print MATRIX_HTML"\n      <td style=\"background-color: rgb($red,$green,$blue); width:12px; height:12px; text-align:center\"><small>$x</small></td>";
						}
					print MATRIX_HTML"\n   </tr>";
					}
				print MATRIX_HTML"</table>";

				# color coding
				print MATRIX_HTML"<br><table style=\"border-spacing: 0px;\"><tr><td><small>min</small></td>";
				$steps=100;
				foreach$i(1..$steps)
					{
					$red=0;
					$green=0;
					$blue=0;
					$rel_value=$i/$steps;
					if($rel_value<=0.5)
						{
						$blue=(int(($rel_value*255)+0.5))*2;
						}
					else
						{
						$blue=255-((int((($rel_value-0.5)*255)+0.5))*2);
						$red=(int((($rel_value-0.5)*255)+0.5))*2;
						}
					print MATRIX_HTML"<td style=\"background-color: rgb($red,$green,$blue); height: 8px;\"> </td>";
					}
				print MATRIX_HTML"<td><small>max</small></td></tr></table>";
				close MATRIX_HTML;
				}
			}
		closedir DIR;

		# POSITIONAL NUCLEOTIDE COMPOSITION / LENGTH DISTRIBUTION
		opendir(DIR,$dump_folder)||die print$!;
		$min_length=0;
		$max_length=0;
		%seqs=();
		%seqs_per_class=();
		%readscounts_per_class=();
		while($file=readdir(DIR))
			{
			if($file=~/\.fas$/)
