							}
						if($min_length==0||length$_<$min_length)
							{
							$min_length=length$_;
							}
						$seqs{$_}=$reads; # save reads for each sequence
						$seqs_per_class{$class}{$_}=$reads;
						$readscounts_per_class{$class}+=$reads;
						}
					}
				close FAS;
				}
			}
		closedir DIR;

		# global
		%length=();
		%pos_nucl_comp=();
		$width=500;
		$height=500;
		$stacked_height=300;
		$curve_type="none"; # use 'function' for smoth curves
		@nucl_order=('A','T','G','C','N');
		foreach$seq(keys%seqs)
			{
