			if(-e"$dump_folder/$storage/pingpong-matrix_$class.html") # add ping pong matrix if file exists
				{
				$html_string.="
		    <div style=\"\">
		  <iframe src=\"$storage/pingpong-matrix_$class.html\" frameborder=\"1\" scrolling=\"no\" style=\"width:625px; height:380px; overflow:hidden;\"></iframe>
		  </div>

		 ";
				}
