sub clean_SeqMap_miRNA
	{
	# step 1: keep perferct hits or hits with non-template 3' ends
	open(SEQMAP,"$dump_folder/SeqMap_$file_id.temp")||die print"\nERROR! Unable to open temporary SeqMap output file $dump_folder/SeqMap_$file_id.temp.\n$!\n\n";
	open(SEQMAP_CLEAN1,">$dump_folder/SeqMap_$file_id.clean-1.temp")||die print"\nERROR! Unable to create clean SeqMap output file.\n$!\n\n";
	$head=<SEQMAP>;
	%best_hits=();
	while(<SEQMAP>)
		{
		$_=~s/\s*$//;
		@seqmap=split("\t",$_);
		if($seqmap[5]==0)
			{
			print SEQMAP_CLEAN1"$_\t0\n"; # 5th column will refer to tail length, 6th column to total mismatch counts!
			# save best alignment
			$best_hits{$seqmap[4]}=0;
			}

		# check number of internal modifications
		else
			{
			$current_internal_mismatch=0;
			foreach$p(1..(length$seqmap[2])-$max_miRtail_length)
				{
				if(substr($seqmap[2],$p-1,1)ne substr($seqmap[4],$p-1,1))
					{
					$current_internal_mismatch++;
					}
				last if($current_internal_mismatch>$intmod_miR);
				}
			}

		### relaxed tailing defenition = mm in one of the last n positions means: tail starts here ###
		if($current_internal_mismatch<=$intmod_miR)
			{
			$tail_present=0;
			foreach$p((length$seqmap[2])-$max_miRtail_length..(length$seqmap[2])-1)
				{
				if(substr($seqmap[2],$p,1) ne substr($seqmap[4],$p,1))
					{
					# check whether tail-nucleotides represent allowed 3' modification, otherwise count it as internal mismatch
					$tail=substr($seqmap[4],$p);
					@tail=split('',$tail);
					$all_ok=1;
					foreach$nt(@tail)
						{
						if(!$miRtails{$nt})
							{
							$all_ok=0;
							last;
							}
						}
					if($all_ok==0)
						{
						$current_internal_mismatch++;
						last if($current_internal_mismatch>$intmod_miR);
						next;
						}

					$tail_present=1;
					$seqmap[5]=length(substr($seqmap[2],$p)); #5th column will refer to tail length, 6th column to total mismatch counts!
					$total_mm_counts=$seqmap[5]+$current_internal_mismatch;
					print SEQMAP_CLEAN1"$seqmap[0]\t$seqmap[1]\t$seqmap[2]\t$seqmap[3]\t$seqmap[4]\t$seqmap[5]\t$total_mm_counts\n";

					# save best alignment
					if(!$best_hits{$seqmap[4]})
						{
						$best_hits{$seqmap[4]}=$total_mm_counts;
						}
					elsif($seqmap[5]<$best_hits{$seqmap[4]})
						{
						$best_hits{$seqmap[4]}=$total_mm_counts;
						}
					last;
					}
				}
			# no (valid) tail present, only internal modifications, yet not printet to clean seqmap file. 5th column will refer to tail length, 6th column to total mismatch counts!
			if($tail_present==0&&$current_internal_mismatch<=$intmod_miR)
				{
				$best_hits{$seqmap[4]}=$seqmap[5];
				print SEQMAP_CLEAN1"$seqmap[0]\t$seqmap[1]\t$seqmap[2]\t$seqmap[3]\t$seqmap[4]\t0\t$seqmap[5]\n";
				}
			}
		}
	close SEQMAP;
	close SEQMAP_CLEAN1;

	# step 2: keep best hits only, replace '-stem-loop' by '-5p/-3p', check internal modificationss
	open(SEQMAP_CLEAN1,"$dump_folder/SeqMap_$file_id.clean-1.temp")||die print"\nERROR! Unable to open temporary SeqMap output file $dump_folder/SeqMap_$file_id.clean-1.temp.\n$!\n\n";
	open(SEQMAP_CLEAN2,">$dump_folder/SeqMap_$file_id.clean-2.temp")||die print"\nERROR! Unable to create clean SeqMap output file $dump_folder/SeqMap_$file_id.clean-2.temp.\n$!\n\n";
	while(<SEQMAP_CLEAN1>)
		{
		$_=~s/\s*$//;
		@seqmap=split("\t",$_);
		if($best_hits{$seqmap[4]}==$seqmap[6])
			{
			# check -5p or -3p
			if($seqmap[1]+((length$seqmap[4])/2)<=$miR_hairpin_length{$seqmap[0]}/2)
				{
				$_=~s/_stem-loop/-5p/
				}
			else
				{
				$_=~s/_stem-loop/-3p/
				}

			# check for internal modifications
			if($seqmap[6]>$seqmap[5])
				{
				$modification_cigar_string="";
				foreach$p(1..(length$seqmap[4])-$max_miRtail_length)
					{
					if(substr($seqmap[2],$p-1,1)ne substr($seqmap[4],$p-1,1))
						{
						$modification_cigar_string.=$p.substr($seqmap[2],$p-1,1)."->".substr($seqmap[4],$p-1,1)."|";
						}
					}
				if(length$modification_cigar_string>0)
					{
					$modification_cigar_string=~s/\|$//;
					$_=~s/\t/($modification_cigar_string)\t/;
					}
				}
			print SEQMAP_CLEAN2"$_\n";
			}
		}
	close SEQMAP_CLEAN1;
	close SEQMAP_CLEAN2;
	undef%best_hits;
	}

sub clean_SeqMap_ncRNA
	{
	open(SEQMAP,"$dump_folder/SeqMap_ncRNA.temp")||die print"\nERROR! Unable to open temporary SeqMap output file $dump_folder/SeqMap_ncRNA.temp.\n$!\n\n";
	$head=<SEQMAP>;
	%best_hits=();
	while(<SEQMAP>)
		{
		@seqmap=split("\t",$_);
		if(!$best_hits{$seqmap[4]}||$seqmap[5]<$best_hits{$seqmap[4]})
			{
			$best_hits{$seqmap[4]}=$seqmap[5];
			}
		}
	close SEQMAP;
	open(SEQMAP,"$dump_folder/SeqMap_ncRNA.temp")||die print"\nERROR! Unable to open temporary SeqMap output file $dump_folder/SeqMap_ncRNA.temp.\n$!\n\n";
	open(SEQMAP_CLEAN,">$dump_folder/SeqMap_ncRNA.clean.temp")||die print"\nERROR! Unable to create clean SeqMap output file $dump_folder/SeqMap_ncRNA.clean.temp.\n$!\n\n";
	$head=<SEQMAP>;
	while(<SEQMAP>)
		{
		@seqmap=split("\t",$_);
		if($seqmap[5]==$best_hits{$seqmap[4]})
			{
			print SEQMAP_CLEAN$_;
			}
		}
	close SEQMAP;
	close SEQMAP_CLEAN;
	undef%best_hits;
	}

sub basic_analysis
	{
	open(IN,$ba_in)||print"\nERROR! Unable to analyze output file $ba_in.\n$!\n\n";
	open(OUT,">$ba_out")||print"\nERROR! Unable to create output file $ba_out.\n$!\n\n";
	%length=();
	%comp=();
	%nt=();
	while(<IN>)
		{
		$_=~s/\s*$//;
		if($_=~s/^>//)
			{
			$reads=$_;
			}
		else
			{
			$length{length$_}+=$reads;
			foreach$p(1..length$_)
				{
				$comp{$p}{substr($_,$p-1,1)}+=$reads;
				$nt{substr($_,$p-1,1)}=1;
				}
			}
		}
	close IN;

	@size=keys%length;
	@size=sort{$a<=>$b}@size;
	@nt=keys%nt;
	@nt=sort@nt;
	print OUT"LENGTH DISTRIBUTION\nlength\treads";
	foreach$l($size[0]..$size[-1])
		{
		print OUT"\n$l\t$length{$l}";
		}

	print OUT"\n\nPOSITIONAL NUCLEOTIDE COMPOSITION\npos.";
	foreach$nt(@nt)
		{
		print OUT"\t$nt";
		}
	foreach$p(1..$size[-1])
