		open(MIR_HAIRPIN,"UNITAS_refdump_$species/miRBase_hairpin")||die print"\nERROR! Unable to open miRBase file UNITAS_refdump_$species/miRBase_hairpin.\n$!\n\n";
		open(MIR_HAIRPIN_SPECIES,">UNITAS_refdump_$species/miRBase_hairpin_$species{$species}.fas")||die print"\nERROR! Unable to create file UNITAS_refdump_$species/miRBase_hairpin_$species{$species}.fas.\n$!\n\n";
		open(MIR_HAIRPIN_NONSPECIES,">UNITAS_refdump_$species/miRBase_hairpin_non-$species{$species}.fas")||die print"\nERROR! Unable to create file UNITAS_refdump_$species/miRBase_hairpin_non-$species{$species}.fas.\n$!\n\n";
		%miRhairpin_species=();
