sub diff_expr
	{
	print"


                 unitas: DIFFERENTIAL EXPRESSION MODULE

    |     1a
  E |   XXXXXX              1c
  X |   XXXXXX     1b     XXXXXX
  P |   XXXXXX   XXXXXX   XXXXXX
  R |   XXXXXX   XXXXXX   XXXXXX
  E |   XXXXXX   XXXXXX   XXXXXX
  S |   XXXXXX   XXXXXX   XXXXXX                      2b
  S |   XXXXXX   XXXXXX   XXXXXX             2a     OOOOOO
  I |   XXXXXX   XXXXXX   XXXXXX           OOOOOO   OOOOOO
  O |   XXXXXX   XXXXXX   XXXXXX           OOOOOO   OOOOOO     2c
  N |   XXXXXX   XXXXXX   XXXXXX           OOOOOO   OOOOOO   OOOOOO
    |   XXXXXX   XXXXXX   XXXXXX           OOOOOO   OOOOOO   OOOOOO
    |___XXXXXX___XXXXXX___XXXXXX___________OOOOOO___OOOOOO___OOOOOO_____
               condition 1                        condition 2

";
	if(@diffex_g1==0||@diffex_g2==0)
		{
		print"\n\nYou must define unitas output folders for each group with -g1 and -g2.\nThe command should look like this:\n\nperl unitas.pl -diffexpr -g1 folderA -g1 folderB -g2 folderC -g2 folderD\n\n";
		exit;
		}
	if($standard_Ttest) # load the Statistics::Distributions module
		{
		$load_module=eval{require Statistics::Distributions;Statistics::Distributions->import();1;};
		if(!$load_module){
		print"

Perl module Statistics::Distributions is required to perform a t-test, yet it
is not installed on this machine! You can download it here:

https://metacpan.org/pod/Statistics::Distributions.

";
		exit;}
		}


	# remove redundant folders from groups
	%diffex_g1=();
	print"\n\nGROUP 1:";
	foreach(sort@diffex_g1)
		{
		$diffex_g1{$_}=1;
		print"\n $_";
		}
	%diffex_g2=();
	print"\n\nGROUP 2:";
	foreach(sort@diffex_g2)
		{
		$diffex_g2{$_}=1;
		print"\n $_";
		}

	# check number of replicates per group
	if(keys%diffex_g1<2||keys%diffex_g2<2)
		{
		if($standard_Ttest&&keys%diffex_g1==1&&keys%diffex_g2==1)
			{
			print"\n\nNOTE: You cannot perform a t-test with only one replicate per group!\nUnitas will perform an alternative SAND test with fictional variance.\nHandle results with caution!";
			undef$standard_Ttest;
			}
		$print_estimated_deviation=$estimated_deviation*100;
		print"\n\nWARNING! There should be at least two replicates in each of the two groups.";
		if($estimated_deviation>0)
			{
			print"\nVariance will be estimated by adding two virtual replicates with rpm values\n+- $print_estimated_deviation%.\n";
			}
		else
			{
			print"\nIf it is not possible to use more replicates, use the option\n-diffexpr_estdev [0..1] to estimate standard deviation.\n";
			}
		}

	# print which test will be performed
	$test_to_perform="'SAND test' (shared area of two normal distributions)";
	if($standard_Ttest)
		{
		$test_to_perform="standard t-test (unpaired, two-sided)";
		}
	print"

unitas will perform a $test_to_perform
with critical value for p<=$diff_expr_p to find differentially expressed small RNAs.

";



	# check for existence of each folder
	foreach(keys%diffex_g1,keys%diffex_g2)
		{
		if(!-d$_)
			{
			print"\nERROR! Folder $_ does not exist.\n\n";
			exit;
			}
		}

	#check for occurence of any folder in both groups
	foreach$fld(keys%diffex_g1)
		{
		if($diffex_g2{$fld})
			{
			print"\nERROR! A replicate must not occur in both groups.\n$f1 was assigned to group 1 AND group 2.\n\n";
			exit;
			}
		}

	# collect sequences and calculate rpm values
	print"\n\nCollect sequences and calculate rpm values...";
	%rpm=();
	%abs_reads=();
	$total_reads_group1=0;
	$total_reads_group2=0;
	foreach$fld(keys%diffex_g1,keys%diffex_g2)
		{
		open(SUMMARY,"$fld/$outfile_prefix.annotation_summary.txt")||die print"\nERROR! Unable to open $fld/$outfile_prefix.annotation_summary.txt.\n$!\n\n";
		print"\nRead data from $fld...";
		while(<SUMMARY>)
			{
			next if($_=~/^low_complexity/);
			next if($_=~/^ /);
			$_=~s/\s*$//;
			@d=split("\t",$_);
			$total_reads{$fld}+=$d[1];
			if($diffex_g1{$fld})
				{
				$total_reads_group1+=$d[1];
				}
			elsif($diffex_g2{$fld})
				{
				$total_reads_group2+=$d[1];
				}
			last if($_=~/^\s*$/);
			}
		close SUMMARY;

		if(-d"$fld/fasta") # since version 1.6.0 unitas puts all the FASTA files into a sub-folder
			{
			opendir(DIR,"$fld/fasta")||die print"\nERROR! Unable to open directory $fld/fasta.\n$!\n\n";
			$fasta_dir="$fld/fasta";
			}
		else
			{
			opendir(DIR,$fld)||die print"\nERROR! Unable to open directory $fld.\n$!\n\n";
			$fasta_dir=$fld;
			}
		while($file=readdir(DIR))
			{
			# open unitas fasta files but not those with special format of fasta headers (where header not or not only refers to sequence read counts)
			if($file=~/^$outfile_prefix\..+\.fas$/&&$file!~/^$outfile_prefix\.non-miR\.fas$/&&$file!~/^$outfile_prefix\.piRcandidates\.fas$/&&$file!~/^$outfile_prefix\.phasiRNA\.sorted\.fas$/)
				{
				$class=$file;
				$class=~s/^$outfile_prefix\.//;
				$class=~s/\.fas$//;
				open(FAS,"$fasta_dir/$file")||die print"\nERROR! Unable to open fasta file $fld/$file.\n$!\n\n";
				while(<FAS>)
					{
					$_=~s/\s*$//;
					if($_=~s/^>//)
						{
						$reads=$_;
						}
					else
						{
						$rpm{$_}{$fld}=$reads/$total_reads{$fld}*1000000;
						$abs_reads{$_}{$fld}=$reads;
						$annotation{$_}{$class}=1;
						}
					}
				close FAS;
				}
			}
		closedir DIR;
		$print_collected_reads=int($total_reads{$fld}+0.5);
		print" OK.\nTotal reads: $print_collected_reads\n";
		}
	$seqs_to_analyze=keys%rpm;
	print"

 Calculate raw p values for differential expression of $seqs_to_analyze sequences
 0%                                  50%                                  100%
 |------------------------------------|------------------------------------|
 ";

	# perform the statistical test for each sequence
	$sig=0;
	$nsig=0;
	$skipped_tests=0;
	$performed_tests=0;
	%uncorrected_p_value=();
	%higher_expression_in_group=();
	$progress=0;
	$progress_dots=0;
	foreach$seq(keys%rpm)
		{
		$progress++;
		if($progress>=$seqs_to_analyze/75)
			{
			print".";
			$progress=0;
			$progress_dots++;
			}
		@i1=();
		@i2=();
		$present_in_all_probes=1;
		foreach$fld_g1(keys%diffex_g1)
			{
			if(!$rpm{$seq}{$fld_g1})
				{
				$rpm{$seq}{$fld_g1}=0;
				$present_in_all_probes=0;
				}
			push(@i1,$rpm{$seq}{$fld_g1});
			}
		foreach$fld_g2(keys%diffex_g2)
			{
			if(!$rpm{$seq}{$fld_g2})
				{
				$rpm{$seq}{$fld_g2}=0;
				$present_in_all_probes=0;
				}
			push(@i2,$rpm{$seq}{$fld_g2});
			}

		if($present_in_all_probes==0&&$diffexpr_nostrays==1)
			{
			$skipped_tests++;
			}
		else
			{
			$performed_tests++;

			# perform a t-test (two-sided)
			if($standard_Ttest)
				{
				# calculate mean for each group
				$avg1=calc_avg(@i1);
				$avg2=calc_avg(@i2);
				$avg_table_out="\t$avg1\t$avg2";

				# test which group has higher avg expression
				$higher_expression_in_group{$seq}=2;
				if($avg1>$avg2){$higher_expression_in_group{$seq}=1;}

				# calculate common variance
				$sum_diff_squares1=0;
				$sum_diff_squares2=0;
				foreach$x(@i1){$sum_diff_squares1+=($x-$avg1)**2;}
				foreach$x(@i2){$sum_diff_squares2+=($x-$avg2)**2;}

				$s_square=(($sum_diff_squares1+$sum_diff_squares2))/((@i1+@i2)-2);
				$t_value=(abs($avg1-$avg2))/(sqrt(($s_square/@i1)+($s_square/@i2)));
				$dof=(@i1+@i2)-2;
				$p_Ttest=Statistics::Distributions::tprob($dof,$t_value);
				$uncorrected_p_value{$seq}=$p_Ttest*2; # p for upper AND lower (two-sided) end of the distribution
				$p_shared=$p_Ttest*2;
				record_test_resultst();
				}

			# calculate shared area of two normal distributions as a proxy for p
			else
				{
				calc_shared_area();
				$uncorrected_p_value{$seq}=$p_shared;
				}
			}
		}
	foreach($progress_dots..74)
		{
		print".";
		}
	print"\n\n$performed_tests tests performed: $sig tests with p<=$diff_expr_p.";

	# Boferroni (1) / Bonferroni-Holm (2) correction of alpha error
	$sig=0;
	%corrected_significant=();
	if($alpha_correction==1)
		{
		print"\nApply Bonferroni-Holm correction of alpha error...";
		$i=-1;
		foreach$seq(sort{$uncorrected_p_value{$a}<=>$uncorrected_p_value{$b}}keys%uncorrected_p_value)
			{
			$i++;
			if($uncorrected_p_value{$seq}*($performed_tests-$i)<=$diff_expr_p||$diff_expr_p>=1)
				{
				$sig++;
				if($uncorrected_p_value{$seq}>1){$uncorrected_p_value{$seq}=1;}
				$corrected_significant{$seq}=$uncorrected_p_value{$seq};
				}
			else
				{
				last;
				}
			}
		foreach$seq(keys%corrected_significant)
			{
			$corrected_significant{$seq}=$corrected_significant{$seq}*($performed_tests-$i);
			}

		print" done. -> $sig significant results.";
		}
	elsif($alpha_correction==2)
		{
		print"\nApply strict Bonferroni correction of alpha error...";
		foreach$seq(keys%uncorrected_p_value)
			{
			$p_corrected=$uncorrected_p_value{$seq}*$performed_tests;
			if($p_corrected<=$diff_expr_p||$diff_expr_p>=1)
				{
				$sig++;
				if($p_corrected>1){$p_corrected=1;}
				$corrected_significant{$seq}=$p_corrected;
				}
			}
		print" done. -> $sig significant results.";
		}
	else
		{
		print"\nCollect results without correction of alpha error...";
		foreach$seq(keys%uncorrected_p_value)
			{
			if($uncorrected_p_value{$seq}<=$diff_expr_p||$diff_expr_p>=1)
				{
				$sig++;
				$corrected_significant{$seq}=$uncorrected_p_value{$seq}; # use uncorrected p values for output
				}
			}
		print" done.";
		}

	# print significant results to output file
	print"\nPrint results to outfiles...";
	print DIFFEX_OUT_1"sequence";
	print DIFFEX_OUT_2"sequence";
	foreach$fld(@diffex_g1,@diffex_g2)
		{
		print DIFFEX_OUT_1"\tabs_$fld";
		print DIFFEX_OUT_2"\tabs_$fld";
		}
	foreach$fld(@diffex_g1,@diffex_g2)
		{
		print DIFFEX_OUT_1"\trpm_$fld";
		print DIFFEX_OUT_2"\trpm_$fld";
		}
	print DIFFEX_OUT_1"\tavg_group1\tavg_group2\tp-value\tcorr_p-value\tclass\n";
	print DIFFEX_OUT_2"\tavg_group1\tavg_group2\tp-value\tcorr_p-value\tclass\n";

	foreach$seq(sort{$a cmp $b}keys%corrected_significant)
		{
		# group 1 > group 2
		if($higher_expression_in_group{$seq}==1)
			{
			print DIFFEX_OUT_1"${seq}$diffexpr_table_out{$seq}\t$uncorrected_p_value{$seq}\t$corrected_significant{$seq}";
			foreach$class(sort{$a cmp $b}keys%{$annotation{$seq}})
				{
				print DIFFEX_OUT_1"\t$class";
				}
			print DIFFEX_OUT_1"\n";
			}
		# group 2 > group 1
		elsif($higher_expression_in_group{$seq}==2)
			{
			print DIFFEX_OUT_2"${seq}$diffexpr_table_out{$seq}\t$uncorrected_p_value{$seq}\t$corrected_significant{$seq}";
			foreach$class(sort{$a cmp $b}keys%{$annotation{$seq}})
				{
				print DIFFEX_OUT_2"\t$class";
				}
			print DIFFEX_OUT_2"\n";
			}
		}
	close DIFFEX_OUT_1;
	close DIFFEX_OUT_2;
	print" done.\n\n\n";

	sub calc_shared_area
		{
		avg_and_dev();
		sub avg_and_dev
			{
			# calculate average and standard deviation
			$avg1=calc_avg(@i1);
			$avg2=calc_avg(@i2);
			$avg_table_out="\t$avg1\t$avg2";
			if(@i1==1&&$estimated_deviation>0) # simulate variance if there is only one replicate
				{
				push(@i1,$i1[0]*(1-$estimated_deviation));
				push(@i1,$i1[0]*(1+$estimated_deviation));
				}
			if(@i2==1&&$estimated_deviation>0) # simulate variance if there is only one replicate
				{
				push(@i2,$i2[0]*(1-$estimated_deviation));
				push(@i2,$i2[0]*(1+$estimated_deviation));
				}
			$avg=$avg1; $stddev1=calc_stddev(@i1);
			$avg=$avg2; $stddev2=calc_stddev(@i2);

			# inverse group1 and group2 (group1 must have lower avg)
			$higher_expression_in_group{$seq}=2;
			if($avg1>$avg2)
				{
				($avg1,$avg2)=($avg2,$avg1);
				($stddev1,$stddev2)=($stddev2,$stddev1);
				$higher_expression_in_group{$seq}=1;
				}

			# use minimal expected standard deviation (corresponds to expected standard deviation in technical replicates -> e.g. resequencing the same library. Depends on rpm and library size)
			if($avg1==0)
				{
				$avg1=1/10**10;
				}
			if($avg2==0)
				{
				$avg2=1/10**10;
				}

			$minimum_relative_stddev1=(1/sqrt($total_reads_group1/1000000))/sqrt($avg1);
			$minimum_relative_stddev2=(1/sqrt($total_reads_group2/1000000))/sqrt($avg2);
			if($stddev1<$minimum_relative_stddev1)
				{
				$stddev1=$minimum_relative_stddev1;
				}
			if($stddev2<$minimum_relative_stddev2)
				{
				$stddev2=$minimum_relative_stddev2;
				}
			}

		# calculate the point of intersection ($xi) of both normal distributions
		if(abs($stddev1-$stddev2)<0.00001)
			{
			$xi=($avg1+$avg2)/2;
			}
		else
			{
			$xi=((($stddev2**2)*$avg1)-(($stddev1**2)*$avg2)+(($stddev1*$stddev2)*(sqrt((($avg1-$avg2)**2)+((2*(($stddev2**2)-($stddev1**2)))*(log($stddev2/$stddev2)))))))/(($stddev2**2)-($stddev1**2));
			}

		# calculate the area spanned by both graphs (=error probability p)
		$A_shared=2*(1-(integral($xi,$avg1,$stddev1)));
		$p_shared=$A_shared/($A_shared+(2*(1-$A_shared)));
		record_test_resultst();
		sub record_test_resultst
			{
			if($p_shared<=$diff_expr_p||$diff_expr_p>=1)
				{
				$sig++;
				foreach$fld(@diffex_g1,@diffex_g2)
					{
					if(!$abs_reads{$seq}{$fld})
						{
						$abs_reads{$seq}{$fld}=0;
						}
					$diffexpr_table_out{$seq}.="\t$abs_reads{$seq}{$fld}";
					}
				foreach$fld(@diffex_g1,@diffex_g2)
					{
					$diffexpr_table_out{$seq}.="\t$rpm{$seq}{$fld}";
					}
				$diffexpr_table_out{$seq}.=$avg_table_out;
				}
			else
				{
				$nsig++;
				}
			}
		}

	sub calc_avg
		{
		$avg=0;
		foreach(@_)
			{
			$avg+=$_;
			}
		return ($avg/@_);
		}
	sub calc_stddev
		{
		$var=0;
		foreach(@_)
			{
			$var+=($_-$avg)**2;
			}
		$var=$var/@_;
		return (sqrt($var));
		}
	sub density
		{
		($x,$m,$s)=(0,0,1);
		$x=shift if@_;
		$m=shift if@_;
		$s=shift if@_;
		$z=($x-$m)/$s;
		return exp(-0.5*$z*$z)/(2.506628274631*$s);
		}
	sub integral
		{
		($x,$m,$s)=(0,0,1);
		$x=shift if@_;
		$m=shift if@_;
		$s=shift if@_;
		$z=($x-$m)/$s;
		$t=1.0/(1+0.2316419*abs($z));
		$y=$t*(0.319381530+$t*(-0.356563782+$t*(1.781477937+$t*(-1.821255978+$t*1.330274429))));
		if($z>0)
			{
			return 1-density($z)*$y;
			}
		else
			{
			return density($z)*$y;
			}
		}
	}
}
